// src/components/AdminLogin.jsx
import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import './AdminLogin.css';

const AdminLogin = ({ onLoginSuccess }) => {
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoginData(prev => ({ ...prev, [name]: value }));
  };

  // CREATE ADMIN USER - Gamit ang common email domains
  const createAdminUser = async () => {
    try {
      setLoading(true);
      setError('');

      // Subukan ang mga ito (isa-isa):
      const testEmails = [
        'admin@gmail.com',           // Common domain
        'admin@yahoo.com',           // Common domain  
        'admin@outlook.com',         // Common domain
        'test.admin@ptc.edu.ph',     // May subdomain
        'admin-ptc@test.com',        // With hyphen
        'admin@ptc-edu.ph'           // Alternative domain
      ];

      let createdUser = null;

      // Subukan ang bawat email hanggang sa gumana
      for (const email of testEmails) {
        try {
          console.log('Trying email:', email);
          
          const { data: authData, error: authError } = await supabase.auth.signUp({
            email: email,
            password: 'demo123',
          });

          if (!authError && authData.user) {
            console.log('‚úÖ Success with email:', email);
            createdUser = { ...authData, email };
            break;
          }
        } catch (err) {
          console.log('Failed with email:', email, err.message);
          continue;
        }
      }

      if (!createdUser) {
        throw new Error('All email attempts failed. Check Supabase email restrictions.');
      }

      // I-add sa admin_users table
      const { error: adminError } = await supabase
        .from('admin_users')
        .insert([
          {
            id: createdUser.user.id,
            email: createdUser.email,
            full_name: 'PTC Administrator',
            role: 'super_admin'
          }
        ]);

      if (adminError && !adminError.message.includes('duplicate key')) {
        console.log('Admin table insert note:', adminError);
      }

      setError(`‚úÖ Admin created with: ${createdUser.email}`);
      setLoginData({ email: createdUser.email, password: 'demo123' });

    } catch (error) {
      console.error('Create admin error:', error);
      setError('Error: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  // MANUAL CREATE - Gamit ang custom email
  const createManualAdmin = async () => {
    const customEmail = prompt('Enter valid email address:', 'admin@gmail.com');
    if (!customEmail) return;

    try {
      setLoading(true);
      
      const { data: authData, error: authError } = await supabase.auth.signUp({
        email: customEmail,
        password: 'demo123',
      });

      if (authError) throw authError;

      if (authData.user) {
        await supabase
          .from('admin_users')
          .insert([
            {
              id: authData.user.id,
              email: customEmail,
              full_name: 'PTC Administrator',
              role: 'super_admin'
            }
          ]);

        setError(`‚úÖ Admin created: ${customEmail}`);
        setLoginData({ email: customEmail, password: 'demo123' });
      }
    } catch (error) {
      setError('Failed: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!loginData.email || !loginData.password) {
      setError('Please fill in all fields');
      return;
    }

    try {
      setLoading(true);
      setError('');

      const { data: authData, error: authError } = await supabase.auth.signInWithPassword({
        email: loginData.email,
        password: loginData.password,
      });

      if (authError) throw authError;
      
      const user = authData?.user;
      if (!user) throw new Error('Authentication failed.');

      // Check admin table
      const { data: adminRow, error: adminError } = await supabase
        .from('admin_users')
        .select('id, full_name, role, email')
        .eq('id', user.id)
        .single();

      if (adminError || !adminRow) {
        await supabase.auth.signOut();
        throw new Error('Access denied. Admin account required.');
      }

      onLoginSuccess && onLoginSuccess({ user, admin: adminRow });

    } catch (err) {
      console.error('Login error:', err);
      setError(err.message || 'Invalid email or password');
    } finally {
      setLoading(false);
    }
  };

  const handleDemoLogin = () => {
    setLoginData({ email: 'admin@gmail.com', password: 'demo123' });
    setError('');
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <div className="logo-container">
            <img src="/logo-ptc.png" alt="PTC Logo" className="logo-image" />
          </div>
          <div className="college-info">
            <h1 className="college-name">Pateros Technological College</h1>
            <p className="login-title">Admin Portal</p>
          </div>
        </div>

        <form onSubmit={handleLogin} className="login-form" noValidate>
          {error && <div className="error-message" role="alert">‚ö†Ô∏è {error}</div>}

          <div className="form-group">
            <label htmlFor="email">Admin Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={loginData.email}
              onChange={handleInputChange}
              placeholder="admin@gmail.com"
              disabled={loading}
              required
              autoComplete="username"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={loginData.password}
              onChange={handleInputChange}
              placeholder="Enter your password"
              disabled={loading}
              required
              autoComplete="current-password"
            />
          </div>

          <button type="submit" className="login-button" disabled={loading}>
            {loading ? 'Signing In...' : 'üîê Sign In'}
          </button>

          <button type="button" className="demo-login-button" onClick={handleDemoLogin}>
            üöÄ Fill Demo Credentials
          </button>

          <button type="button" className="create-admin-button" onClick={createAdminUser} disabled={loading}>
            ‚ö° Auto Create Admin (Multiple Emails)
          </button>

          <button type="button" className="manual-create-button" onClick={createManualAdmin} disabled={loading}
            style={{ marginTop: '5px', background: '#17a2b8', fontSize: '12px', padding: '8px' }}>
            ‚úèÔ∏è Manual Create (Custom Email)
          </button>
        </form>

        <div className="login-footer">
          <p>Restricted Access - Authorized Personnel Only</p>
          <p style={{ fontSize: '12px', color: '#666', marginTop: '5px' }}>
            Use common email domains (gmail, yahoo, etc.)
          </p>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
