// src/components/AdminLogin.jsx
import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';
import './AdminLogin.css';

const AdminLogin = ({ onLoginSuccess }) => {
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [debugInfo, setDebugInfo] = useState('');

  // Clear errors when user starts typing
  useEffect(() => {
    if (error) setError('');
  }, [loginData.email, loginData.password]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoginData(prev => ({ ...prev, [name]: value }));
  };

  // Test connection and debug info
  const testConnection = async () => {
    try {
      const { data, error } = await supabase.auth.getSession();
      console.log('Connection test:', { data, error });
      setDebugInfo(`Connection: ${error ? 'Failed' : 'OK'}`);
    } catch (err) {
      console.error('Connection test error:', err);
      setDebugInfo('Connection: Failed');
    }
  };

  // Create test admin (for development only)
  const createTestAdmin = async () => {
    try {
      setLoading(true);
      setError('');
      setDebugInfo('Creating test admin...');

      // Sign up in Auth
      const { data: authData, error: authError } = await supabase.auth.signUp({
        email: 'admin@ptc.edu.ph',
        password: 'demo123',
      });

      if (authError) throw authError;

      if (authData.user) {
        // Add to admin_users table
        const { error: adminError } = await supabase
          .from('admin_users')
          .insert([
            {
              id: authData.user.id,
              email: 'admin@ptc.edu.ph',
              full_name: 'PTC Administrator',
              role: 'super_admin'
            }
          ]);

        if (adminError) {
          // If insert fails, maybe admin already exists
          console.log('Admin might already exist:', adminError);
        }

        setDebugInfo('Test admin created! You can now login.');
        setLoginData({ email: 'admin@ptc.edu.ph', password: 'demo123' });
      }
    } catch (error) {
      console.error('Create admin error:', error);
      setError('Failed to create test admin: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    // Validation
    if (!loginData.email.trim() || !loginData.password) {
      setError('Please fill in all fields');
      return;
    }

    if (!loginData.email.includes('@')) {
      setError('Please enter a valid email address');
      return;
    }

    try {
      setLoading(true);
      setError('');
      setDebugInfo('Attempting login...');

      // Step 1: Supabase Authentication
      const { data: authData, error: authError } = await supabase.auth.signInWithPassword({
        email: loginData.email.trim().toLowerCase(),
        password: loginData.password,
      });

      if (authError) {
        // Specific error handling
        if (authError.message.includes('Invalid login credentials')) {
          throw new Error('Invalid email or password. Please check your credentials.');
        } else if (authError.message.includes('Email not confirmed')) {
          throw new Error('Please verify your email address before logging in.');
        } else {
          throw authError;
        }
      }

      if (!authData?.user) {
        throw new Error('Authentication failed. Please try again.');
      }

      setDebugInfo('Authentication successful, checking admin privileges...');

      // Step 2: Check admin_users table
      const { data: adminRow, error: adminError } = await supabase
        .from('admin_users')
        .select('id, full_name, role, email, created_at')
        .eq('id', authData.user.id)
        .single();

      if (adminError) {
        console.error('Admin check error:', adminError);
        // If no admin record found, sign out and show error
        await supabase.auth.signOut();
        throw new Error('Access denied. Administrator account required.');
      }

      if (!adminRow) {
        await supabase.auth.signOut();
        throw new Error('Access denied. Your account is not authorized for admin access.');
      }

      setDebugInfo('Login successful! Redirecting...');

      // Step 3: Success - pass user and admin data to parent
      onLoginSuccess({
        user: authData.user,
        admin: adminRow,
        session: authData.session
      });

    } catch (err) {
      console.error('Login error:', err);
      setError(err.message || 'An unexpected error occurred. Please try again.');
      
      // Auto-clear error after 5 seconds
      setTimeout(() => setError(''), 5000);
    } finally {
      setLoading(false);
    }
  };

  const handleDemoLogin = () => {
    setLoginData({ email: 'admin@ptc.edu.ph', password: 'demo123' });
    setError('');
    setDebugInfo('Demo credentials filled. Click "Sign In" to login.');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !loading) {
      handleLogin(e);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <div className="logo-container">
            <img 
              src="/logo-ptc.png" 
              alt="PTC Logo" 
              className="logo-image"
              onError={(e) => {
                e.target.style.display = 'none';
              }}
            />
          </div>
          <div className="college-info">
            <h1 className="college-name">Pateros Technological College</h1>
            <p className="login-title">Admin Portal</p>
          </div>
        </div>

        {/* Debug info - remove in production */}
        {debugInfo && (
          <div className="debug-info" style={{ 
            fontSize: '12px', 
            color: '#666', 
            textAlign: 'center', 
            margin: '10px 0',
            padding: '5px',
            backgroundColor: '#f5f5f5',
            borderRadius: '4px'
          }}>
            {debugInfo}
          </div>
        )}

        <form onSubmit={handleLogin} className="login-form" noValidate>
          {error && (
            <div className="error-message" role="alert">
              ‚ö†Ô∏è {error}
            </div>
          )}

          <div className="form-group">
            <label htmlFor="email">Admin Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={loginData.email}
              onChange={handleInputChange}
              onKeyPress={handleKeyPress}
              placeholder="admin@ptc.edu.ph"
              disabled={loading}
              required
              autoComplete="username"
              autoFocus
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={loginData.password}
              onChange={handleInputChange}
              onKeyPress={handleKeyPress}
              placeholder="Enter your password"
              disabled={loading}
              required
              autoComplete="current-password"
            />
          </div>

          <button 
            type="submit" 
            className="login-button" 
            disabled={loading || !loginData.email || !loginData.password}
          >
            {loading ? (
              <>
                <div className="loading-spinner-small" aria-hidden="true"></div>
                Signing In...
              </>
            ) : (
              'üîê Sign In'
            )}
          </button>

          {/* Development helpers - remove in production */}
          <div className="dev-buttons">
            <button 
              type="button" 
              className="demo-login-button" 
              onClick={handleDemoLogin} 
              disabled={loading}
            >
              üöÄ Fill Demo Credentials
            </button>
            
            <button 
              type="button" 
              className="test-connection-button" 
              onClick={testConnection}
              style={{ 
                marginTop: '8px',
                background: '#6c757d',
                fontSize: '12px',
                padding: '6px 12px'
              }}
            >
              üîß Test Connection
            </button>
            
            <button 
              type="button" 
              className="create-admin-button" 
              onClick={createTestAdmin}
              disabled={loading}
              style={{ 
                marginTop: '8px',
                background: '#dc3545',
                fontSize: '12px',
                padding: '6px 12px'
              }}
            >
              ‚ö° Create Test Admin
            </button>
          </div>
        </form>

        <div className="login-footer">
          <p>Restricted Access - Authorized Personnel Only</p>
          <p style={{ fontSize: '12px', color: '#999', marginTop: '5px' }}>
            Contact IT Support for account issues
          </p>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
