import React, { useEffect, useMemo, useState } from 'react';
import { supabase } from '../supabaseClient';

const SubjectsPage = () => {
  const [subjects, setSubjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const [searchTerm, setSearchTerm] = useState('');
  const [courseFilter, setCourseFilter] = useState('bsit');
  const [yearFilter, setYearFilter] = useState('all');
  const [semesterFilter, setSemesterFilter] = useState('all');

  const [saving, setSaving] = useState(false);
  const [editingSubject, setEditingSubject] = useState(null);

  // Available courses and semesters
  const courses = [
    { id: 'bsit', name: 'BS Information Technology' },
    { id: 'bscs', name: 'BS Computer Science' },
    { id: 'bsis', name: 'BS Information Systems' },
    { id: 'bsemc', name: 'BS Entertainment and Media Computing' }
  ];

  const semesters = ['1st', '2nd', 'Summer'];
  const yearLevels = [
    { value: '1', label: '1st Year' },
    { value: '2', label: '2nd Year' },
    { value: '3', label: '3rd Year' },
    { value: '4', label: '4th Year' }
  ];

  // Form state
  const [form, setForm] = useState({
    subject_code: '',
    subject_name: '',
    description: '',
    units: '',
    course: 'bsit',
    year_level: '',
    semester: '1st',
    lec_hours: '',
    lab_hours: '',
    prerequisites: '',
    corequisites: ''
  });

  // --- LOAD SUBJECTS ---
  useEffect(() => {
    fetchSubjects();
  }, []);

  const fetchSubjects = async () => {
    try {
      setLoading(true);
      setError('');

      const { data, error } = await supabase
        .from('subjects')
        .select('*')
        .order('course', { ascending: true })
        .order('year_level', { ascending: true })
        .order('semester', { ascending: true })
        .order('subject_code', { ascending: true });

      if (error) throw error;
      setSubjects(data || []);
    } catch (err) {
      console.error('Error loading subjects', err);
      setError(err.message || 'Failed to load subjects');
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setEditingSubject(null);
    setForm({
      subject_code: '',
      subject_name: '',
      description: '',
      units: '',
      course: 'bsit',
      year_level: '',
      semester: '1st',
      lec_hours: '',
      lab_hours: '',
      prerequisites: '',
      corequisites: ''
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // --- SUBMIT ADD / UPDATE ---
  const handleSubmit = async (e) => {
    e.preventDefault();

    const {
      subject_code,
      subject_name,
      description,
      units,
      course,
      year_level,
      semester,
      lec_hours,
      lab_hours,
      prerequisites,
      corequisites
    } = form;

    if (!subject_code.trim() || !subject_name.trim() || !course || !year_level || !semester) {
      alert('Please fill required fields.');
      return;
    }

    const payload = {
      subject_code: subject_code.trim(),
      subject_name: subject_name.trim(),
      description: description.trim() || null,
      units: units !== '' ? Number(units) : null,
      course: course,
      year_level: Number(year_level),
      semester: semester,
      lec_hours: lec_hours !== '' ? Number(lec_hours) : null,
      lab_hours: lab_hours !== '' ? Number(lab_hours) : null,
      prerequisites: prerequisites.trim() || null,
      corequisites: corequisites.trim() || null,
    };

    try {
      setSaving(true);

      if (editingSubject) {
        const { data, error } = await supabase
          .from('subjects')
          .update(payload)
          .eq('id', editingSubject.id)
          .select()
          .single();

        if (error) throw error;
        setSubjects((prev) => prev.map((s) => (s.id === editingSubject.id ? data : s)));
        resetForm();
        alert('Subject updated successfully.');
      } else {
        const { data, error } = await supabase
          .from('subjects')
          .insert(payload)
          .select()
          .single();

        if (error) throw error;
        setSubjects((prev) => [...prev, data]);
        resetForm();
        alert('Subject added successfully.');
      }
    } catch (err) {
      console.error('Error saving subject', err);
      alert(err.message || 'Failed to save subject');
    } finally {
      setSaving(false);
    }
  };

  const handleEdit = (subject) => {
    setEditingSubject(subject);
    setForm({
      subject_code: subject.subject_code || '',
      subject_name: subject.subject_name || '',
      description: subject.description || '',
      units: subject.units?.toString() || '',
      course: subject.course || 'bsit',
      year_level: subject.year_level?.toString() || '',
      semester: subject.semester || '1st',
      lec_hours: subject.lec_hours?.toString() || '',
      lab_hours: subject.lab_hours?.toString() || '',
      prerequisites: subject.prerequisites || '',
      corequisites: subject.corequisites || ''
    });
  };

  const handleDelete = async (subject) => {
    const confirm = window.confirm(
      `Delete subject ${subject.subject_code} - ${subject.subject_name}?`
    );
    if (!confirm) return;

    try {
      const { error } = await supabase
        .from('subjects')
        .delete()
        .eq('id', subject.id);

      if (error) throw error;
      setSubjects((prev) => prev.filter((s) => s.id !== subject.id));
    } catch (err) {
      console.error('Error deleting subject', err);
      alert(err.message || 'Failed to delete subject');
    }
  };

  // --- FILTERED SUBJECTS LIST ---
  const filteredSubjects = useMemo(() => {
    const term = searchTerm.trim().toLowerCase();

    return subjects.filter((s) => {
      if (
        term &&
        !(
          s.subject_code?.toLowerCase().includes(term) ||
          s.subject_name?.toLowerCase().includes(term) ||
          s.description?.toLowerCase().includes(term)
        )
      ) {
        return false;
      }

      if (courseFilter !== 'all' && s.course !== courseFilter) {
        return false;
      }

      if (yearFilter !== 'all' && s.year_level.toString() !== yearFilter) {
        return false;
      }

      if (semesterFilter !== 'all' && s.semester !== semesterFilter) {
        return false;
      }

      return true;
    });
  }, [subjects, searchTerm, courseFilter, yearFilter, semesterFilter]);

  // Group subjects by course, year level, and semester for organized display
  const groupedSubjects = useMemo(() => {
    const grouped = {};
    
    filteredSubjects.forEach(subject => {
      const key = `${subject.course}-${subject.year_level}-${subject.semester}`;
      if (!grouped[key]) {
        grouped[key] = {
          course: subject.course,
          year_level: subject.year_level,
          semester: subject.semester,
          subjects: []
        };
      }
      grouped[key].subjects.push(subject);
    });

    // Sort groups logically
    return Object.values(grouped).sort((a, b) => {
      const courseOrder = courses.map(c => c.id);
      const yearOrder = yearLevels.map(y => y.value);
      const semOrder = semesters;
      
      const courseCompare = courseOrder.indexOf(a.course) - courseOrder.indexOf(b.course);
      if (courseCompare !== 0) return courseCompare;
      
      const yearCompare = yearOrder.indexOf(a.year_level.toString()) - yearOrder.indexOf(b.year_level.toString());
      if (yearCompare !== 0) return yearCompare;
      
      return semOrder.indexOf(a.semester) - semOrder.indexOf(b.semester);
    });
  }, [filteredSubjects]);

  // Calculate totals for each group
  const calculateGroupTotals = (subjects) => {
    return subjects.reduce((totals, subject) => {
      totals.units += Number(subject.units) || 0;
      totals.lec_hours += Number(subject.lec_hours) || 0;
      totals.lab_hours += Number(subject.lab_hours) || 0;
      totals.total_hours = totals.lec_hours + totals.lab_hours;
      totals.subject_count = subjects.length;
      return totals;
    }, { units: 0, lec_hours: 0, lab_hours: 0, total_hours: 0, subject_count: 0 });
  };

  return (
    <div className="dashboard-card" style={{ marginTop: '0.5rem' }}>
      <div className="card-header">
        <h3>Subjects Masterlist</h3>
        <span style={{ fontSize: '0.75rem', color: '#4b5563' }}>
          Manage subjects organized by course, year level, and semester.
        </span>
      </div>

      {/* ERROR */}
      {error && (
        <div className="no-data" style={{ marginBottom: '0.5rem', color: '#b91c1c' }}>
          {error}
        </div>
      )}

      {/* FILTERS */}
      <div
        className="dashboard-controls"
        style={{ marginBottom: '0.5rem', gap: '1rem' }}
      >
        <div className="search-box">
          <span className="search-icon">🔍</span>
          <input
            type="text"
            placeholder="Search subject code / name / description..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <div className="filter-controls" style={{ flexWrap: 'wrap', gap: '0.5rem' }}>
          <select
            value={courseFilter}
            onChange={(e) => setCourseFilter(e.target.value)}
            className="status-filter"
          >
            <option value="all">All Courses</option>
            {courses.map(course => (
              <option key={course.id} value={course.id}>
                {course.name}
              </option>
            ))}
          </select>

          <select
            value={yearFilter}
            onChange={(e) => setYearFilter(e.target.value)}
            className="status-filter"
          >
            <option value="all">All Year Levels</option>
            {yearLevels.map(year => (
              <option key={year.value} value={year.value}>
                {year.label}
              </option>
            ))}
          </select>

          <select
            value={semesterFilter}
            onChange={(e) => setSemesterFilter(e.target.value)}
            className="status-filter"
          >
            <option value="all">All Semesters</option>
            {semesters.map(sem => (
              <option key={sem} value={sem}>{sem} Sem</option>
            ))}
          </select>
        </div>
      </div>

      {/* SUBJECTS DISPLAY - GROUPED VIEW */}
      <div style={{ marginBottom: '1rem' }}>
        {loading ? (
          <div className="no-data">Loading subjects...</div>
        ) : groupedSubjects.length === 0 ? (
          <div className="no-data">
            {subjects.length === 0 ? (
              <div>
                No subjects found in database. <br />
                <span style={{ fontSize: '0.875rem', color: '#6b7280' }}>
                  Add subjects using the form below or import the BSIT curriculum.
                </span>
              </div>
            ) : (
              'No subjects match the filters.'
            )}
          </div>
        ) : (
          groupedSubjects.map((group, index) => {
            const totals = calculateGroupTotals(group.subjects);
            const courseName = courses.find(c => c.id === group.course)?.name || group.course;
            const yearLabel = yearLevels.find(y => y.value === group.year_level.toString())?.label || `${group.year_level} Year`;
            
            return (
              <div key={index} className="dashboard-card micro" style={{ marginBottom: '1.5rem' }}>
                <div className="card-header micro" style={{ backgroundColor: '#f8fafc' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                    <div>
                      <h4 style={{ margin: 0 }}>
                        {courseName} - {yearLabel} - {group.semester} Semester
                      </h4>
                      <span style={{ fontSize: '0.75rem', color: '#4b5563' }}>
                        {totals.subject_count} subjects • {totals.units} units • {totals.lec_hours} LEC hrs • {totals.lab_hours} LAB hrs • {totals.total_hours} total hrs
                      </span>
                    </div>
                    <div style={{ fontSize: '0.875rem', fontWeight: '600', color: '#059669' }}>
                      {totals.units} Units
                    </div>
                  </div>
                </div>

                <div className="applications-table-container">
                  <table className="applications-table">
                    <thead>
                      <tr>
                        <th>Code</th>
                        <th>Subject Name</th>
                        <th>Units</th>
                        <th>LEC</th>
                        <th>LAB</th>
                        <th>Prerequisites</th>
                        <th style={{ width: '120px' }}>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {group.subjects.map((subject) => (
                        <tr key={subject.id}>
                          <td>
                            <strong>{subject.subject_code}</strong>
                          </td>
                          <td>
                            <div>
                              <div style={{ fontWeight: '600' }}>{subject.subject_name}</div>
                              {subject.description && subject.description !== subject.subject_name && (
                                <div style={{ fontSize: '0.75rem', color: '#6b7280' }}>
                                  {subject.description}
                                </div>
                              )}
                            </div>
                          </td>
                          <td>{subject.units || '—'}</td>
                          <td>{subject.lec_hours ?? '—'}</td>
                          <td>{subject.lab_hours ?? '—'}</td>
                          <td>
                            <span style={{ 
                              fontSize: '0.75rem', 
                              color: subject.prerequisites && subject.prerequisites !== 'None' ? '#dc2626' : '#6b7280'
                            }}>
                              {subject.prerequisites || 'None'}
                            </span>
                          </td>
                          <td>
                            <div className="action-buttons">
                              <button
                                type="button"
                                className="btn-icon"
                                title="Edit subject"
                                onClick={() => handleEdit(subject)}
                              >
                                ✏️
                              </button>
                              <button
                                type="button"
                                className="btn-icon btn-delete"
                                title="Delete subject"
                                onClick={() => handleDelete(subject)}
                              >
                                🗑️
                              </button>
                            </div>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            );
          })
        )}
      </div>

      {/* FORM: ADD / EDIT SUBJECT */}
      <div className="dashboard-card micro">
        <div className="card-header micro">
          <h3>{editingSubject ? 'Edit Subject' : 'Add New Subject'}</h3>
          {editingSubject && (
            <button
              type="button"
              className="btn micro btn-secondary"
              onClick={resetForm}
            >
              Cancel Edit
            </button>
          )}
        </div>

        <form
          onSubmit={handleSubmit}
          style={{ display: 'flex', flexDirection: 'column', gap: '0.6rem' }}
        >
          {/* Code + Name */}
          <div
            style={{
              display: 'grid',
              gridTemplateColumns: '1fr 2fr',
              gap: '0.5rem',
            }}
          >
            <div>
              <label className="form-label">
                Subject Code *
              </label>
              <input
                name="subject_code"
                type="text"
                className="event-input micro"
                placeholder="e.g. CC 101"
                value={form.subject_code}
                onChange={handleChange}
                required
              />
            </div>

            <div>
              <label className="form-label">
                Subject Name *
              </label>
              <input
                name="subject_name"
                type="text"
                className="event-input micro"
                placeholder="e.g. IT Fundamentals"
                value={form.subject_name}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          {/* Description */}
          <div>
            <label className="form-label">
              Description (optional)
            </label>
            <textarea
              name="description"
              className="event-input micro"
              style={{ minHeight: '60px', resize: 'vertical' }}
              placeholder="Short description of the subject..."
              value={form.description}
              onChange={handleChange}
            />
          </div>

          {/* Course + Year + Sem + Units */}
          <div
            style={{
              display: 'grid',
              gridTemplateColumns: '1fr 1fr 1fr 1fr',
              gap: '0.5rem',
            }}
          >
            <div>
              <label className="form-label">
                Course *
              </label>
              <select
                name="course"
                className="status-select"
                value={form.course}
                onChange={handleChange}
                required
              >
                {courses.map(course => (
                  <option key={course.id} value={course.id}>
                    {course.name}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="form-label">
                Year Level *
              </label>
              <select
                name="year_level"
                className="status-select"
                value={form.year_level}
                onChange={handleChange}
                required
              >
                <option value="">Select Year</option>
                {yearLevels.map(year => (
                  <option key={year.value} value={year.value}>
                    {year.label}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="form-label">
                Semester *
              </label>
              <select
                name="semester"
                className="status-select"
                value={form.semester}
                onChange={handleChange}
                required
              >
                {semesters.map(sem => (
                  <option key={sem} value={sem}>{sem} Sem</option>
                ))}
              </select>
            </div>

            <div>
              <label className="form-label">
                Units *
              </label>
              <input
                name="units"
                type="number"
                min="0"
                step="1"
                className="event-input micro"
                placeholder="e.g. 3"
                value={form.units}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          {/* LEC/LAB + Prerequisites */}
          <div
            style={{
              display: 'grid',
              gridTemplateColumns: '1fr 1fr 1fr 1fr',
              gap: '0.5rem',
            }}
          >
            <div>
              <label className="form-label">
                LEC Hours
              </label>
              <input
                name="lec_hours"
                type="number"
                min="0"
                step="1"
                className="event-input micro"
                placeholder="e.g. 2"
                value={form.lec_hours}
                onChange={handleChange}
              />
            </div>

            <div>
              <label className="form-label">
                LAB Hours
              </label>
              <input
                name="lab_hours"
                type="number"
                min="0"
                step="1"
                className="event-input micro"
                placeholder="e.g. 1"
                value={form.lab_hours}
                onChange={handleChange}
              />
            </div>

            <div>
              <label className="form-label">
                Prerequisites
              </label>
              <input
                name="prerequisites"
                type="text"
                className="event-input micro"
                placeholder="e.g. CC 102"
                value={form.prerequisites}
                onChange={handleChange}
              />
            </div>

            <div>
              <label className="form-label">
                Corequisites
              </label>
              <input
                name="corequisites"
                type="text"
                className="event-input micro"
                placeholder="e.g. CC 103"
                value={form.corequisites}
                onChange={handleChange}
              />
            </div>
          </div>

          <div style={{ marginTop: '0.5rem' }}>
            <button
              type="submit"
              className="btn micro btn-primary"
              disabled={saving}
            >
              {saving
                ? 'Saving...'
                : editingSubject
                ? 'Update Subject'
                : 'Add Subject'}
            </button>
          </div>
        </form>
      </div>

      <style jsx>{`
        .form-label {
          font-size: 0.75rem;
          font-weight: 600;
          display: block;
          margin-bottom: 0.25rem;
        }
      `}</style>
    </div>
  );
};

export default SubjectsPage;
