// src/components/AdminLogin.jsx
import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import './AdminLogin.css';

const AdminLogin = ({ onLoginSuccess }) => {
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoginData(prev => ({ ...prev, [name]: value }));
  };

  // SIMPLE DATABASE AUTH - No Supabase Auth needed
  const handleLogin = async (e) => {
    e.preventDefault();

    if (!loginData.email || !loginData.password) {
      setError('Please fill in all fields');
      return;
    }

    try {
      setLoading(true);
      setError('');

      // 1) Check if admin exists in admin_users table with matching credentials
      const { data: adminRow, error: adminError } = await supabase
        .from('admin_users')
        .select('id, full_name, role, email, password') // Make sure you have password field
        .eq('email', loginData.email)
        .eq('password', loginData.password) // Simple password check
        .single();

      if (adminError || !adminRow) {
        throw new Error('Invalid email or password');
      }

      // 2) SUCCESS - Create simple user object
      const user = {
        id: adminRow.id,
        email: adminRow.email,
        user_metadata: {
          full_name: adminRow.full_name
        }
      };

      const admin = {
        id: adminRow.id,
        full_name: adminRow.full_name,
        role: adminRow.role,
        email: adminRow.email
      };

      // 3) Pass to parent component
      onLoginSuccess && onLoginSuccess({ user, admin });

    } catch (err) {
      console.error('Login error:', err);
      setError(err.message || 'Invalid credentials');
    } finally {
      setLoading(false);
    }
  };

  // CREATE DEFAULT ADMIN (one-time setup)
  const createDefaultAdmin = async () => {
    try {
      setLoading(true);
      
      // Check if admin already exists
      const { data: existingAdmin } = await supabase
        .from('admin_users')
        .select('id')
        .eq('email', 'admin@ptc.edu.ph')
        .single();

      if (existingAdmin) {
        setError('Admin user already exists. Use: admin@ptc.edu.ph / demo123');
        setLoginData({ email: 'admin@ptc.edu.ph', password: 'demo123' });
        return;
      }

      // Create new admin
      const { error: insertError } = await supabase
        .from('admin_users')
        .insert([
          {
            email: 'admin@ptc.edu.ph',
            password: 'demo123', // Plain text password (for demo only)
            full_name: 'PTC Administrator',
            role: 'super_admin'
          }
        ]);

      if (insertError) throw insertError;

      setError('‚úÖ Default admin created! Use: admin@ptc.edu.ph / demo123');
      setLoginData({ email: 'admin@ptc.edu.ph', password: 'demo123' });

    } catch (error) {
      console.error('Create admin error:', error);
      setError('Failed to create admin: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDemoLogin = () => {
    setLoginData({ email: 'admin@ptc.edu.ph', password: 'demo123' });
    setError('');
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <div className="logo-container">
            <img src="/logo-ptc.png" alt="PTC Logo" className="logo-image" />
          </div>
          <div className="college-info">
            <h1 className="college-name">Pateros Technological College</h1>
            <p className="login-title">Admin Portal</p>
          </div>
        </div>

        <form onSubmit={handleLogin} className="login-form" noValidate>
          {error && (
            <div className="error-message" role="alert">
              {error.includes('‚úÖ') ? '‚úÖ' : '‚ö†Ô∏è'} {error}
            </div>
          )}

          <div className="form-group">
            <label htmlFor="email">Admin Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={loginData.email}
              onChange={handleInputChange}
              placeholder="admin@ptc.edu.ph"
              disabled={loading}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={loginData.password}
              onChange={handleInputChange}
              placeholder="demo123"
              disabled={loading}
              required
            />
          </div>

          <button type="submit" className="login-button" disabled={loading}>
            {loading ? (
              <>
                <div className="loading-spinner-small" aria-hidden="true"></div>
                Signing In...
              </>
            ) : (
              'üîê Sign In'
            )}
          </button>

          <button 
            type="button" 
            className="demo-login-button" 
            onClick={handleDemoLogin} 
            disabled={loading}
          >
            üöÄ Fill Demo Credentials
          </button>

          <button 
            type="button" 
            onClick={createDefaultAdmin} 
            disabled={loading}
            style={{
              background: '#28a745',
              width: '100%',
              padding: '12px',
              marginTop: '10px'
            }}
          >
            ‚ö° Create Default Admin (First Time Only)
          </button>
        </form>

        <div className="login-footer">
          <p>Restricted Access - Authorized Personnel Only</p>
          <p style={{ fontSize: '12px', color: '#666', marginTop: '5px' }}>
            Step 1: Click "Create Default Admin"<br/>
            Step 2: Use credentials above<br/>
            Step 3: Click "Sign In"
          </p>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
