// src/components/AdminLogin.jsx
import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';
import './AdminLogin.css';

const AdminLogin = ({ onLoginSuccess }) => {
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Subtle particle animation
  useEffect(() => {
    const canvas = document.getElementById('particleCanvas');
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    const particles = [];
    const particleCount = 12;

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };
    
    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    const colors = [
      'rgba(255, 255, 255, 0.8)',
      'rgba(248, 250, 252, 0.7)',
      'rgba(241, 245, 249, 0.6)',
    ];

    class Particle {
      constructor() {
        this.reset();
        this.y = Math.random() * canvas.height;
      }

      reset() {
        this.x = Math.random() * canvas.width;
        this.y = -20;
        this.size = Math.random() * 15 + 8;
        this.speed = Math.random() * 1.5 + 0.5;
        this.color = colors[Math.floor(Math.random() * colors.length)];
        this.wobble = Math.random() * 1.5;
        this.wobbleSpeed = Math.random() * 0.03 + 0.01;
        this.angle = Math.random() * Math.PI * 2;
      }

      update() {
        this.y += this.speed;
        this.angle += this.wobbleSpeed;
        this.x += Math.sin(this.angle) * this.wobble;

        if (this.y > canvas.height + 20) {
          this.reset();
        }
      }

      draw() {
        ctx.save();
        ctx.translate(this.x, this.y);
        
        ctx.rotate(this.angle * 0.2);
        ctx.fillStyle = this.color;
        ctx.beginPath();
        ctx.ellipse(0, 0, this.size * 0.5, this.size * 0.7, 0, 0, Math.PI * 2);
        ctx.fill();

        ctx.restore();
      }
    }

    for (let i = 0; i < particleCount; i++) {
      particles.push(new Particle());
    }

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      ctx.fillStyle = 'rgba(248, 250, 252, 0.05)';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      particles.forEach(particle => {
        particle.update();
        particle.draw();
      });

      requestAnimationFrame(animate);
    };

    animate();

    return () => {
      window.removeEventListener('resize', resizeCanvas);
    };
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoginData(prev => ({ ...prev, [name]: value }));
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!loginData.email || !loginData.password) {
      setError('Please fill in all fields');
      return;
    }

    try {
      setLoading(true);
      setError('');

      const { data: adminRow, error: adminError } = await supabase
        .from('admin_users')
        .select('*')
        .eq('email', loginData.email)
        .eq('password', loginData.password)
        .single();

      if (adminError) {
        if (adminError.code === 'PGRST116') {
          throw new Error('Invalid email or password');
        }
        throw adminError;
      }

      if (!adminRow) {
        throw new Error('Invalid email or password');
      }

      const user = {
        id: adminRow.id,
        email: adminRow.email,
        user_metadata: {
          full_name: adminRow.full_name
        }
      };

      const admin = {
        id: adminRow.id,
        full_name: adminRow.full_name,
        role: adminRow.role,
        email: adminRow.email
      };

      onLoginSuccess && onLoginSuccess({ user, admin });

    } catch (err) {
      setError(err.message || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  const createDefaultAdmin = async () => {
    try {
      setLoading(true);
      setError('');

      const { data: testData, error: testError } = await supabase
        .from('admin_users')
        .select('email')
        .limit(1);

      if (testError && testError.code === '42703') {
        setError(
          'Table structure is incorrect. Please run the SQL in Supabase SQL Editor to recreate the table with correct structure.'
        );
        return;
      }

      const { data: existingAdmin } = await supabase
        .from('admin_users')
        .select('id')
        .eq('email', 'admin@ptc.edu.ph')
        .single();

      if (existingAdmin) {
        setError('Admin user already exists. Use: admin@ptc.edu.ph / demo123');
        setLoginData({ email: 'admin@ptc.edu.ph', password: 'demo123' });
        return;
      }

      const { error: insertError } = await supabase
        .from('admin_users')
        .insert([
          {
            email: 'admin@ptc.edu.ph',
            password: 'demo123',
            full_name: 'PTC Administrator',
            role: 'super_admin'
          }
        ]);

      if (insertError) throw insertError;

      setError('Default admin created successfully. Use: admin@ptc.edu.ph / demo123');
      setLoginData({ email: 'admin@ptc.edu.ph', password: 'demo123' });

    } catch (error) {
      setError('Failed to create admin: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDemoLogin = () => {
    setLoginData({ email: 'admin@ptc.edu.ph', password: 'demo123' });
    setError('');
  };

  const checkTableStructure = async () => {
    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('admin_users')
        .select('*')
        .limit(1);

      if (error) {
        setError('Table error: ' + error.message);
      } else {
        setError('Table structure is valid. Columns: ' + (data[0] ? Object.keys(data[0]).join(', ') : 'No data'));
      }
    } catch (err) {
      setError('Check failed: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <canvas 
        id="particleCanvas" 
        className="particle-canvas"
      />
      
      <div className="logo-section">
        <div className="logo-container-large">
          <img src="/logo-ptc.png" alt="PTC Logo" className="logo-image-large" />
        </div>
        <div className="college-info-large">
          <h1 className="college-name-large">Pateros Technological College</h1>
          <p className="institution-tagline">Excellence in Technological Education</p>
        </div>
      </div>

      <div className="login-card">
        <div className="login-header">
          <h1 className="login-title">Administrator Portal</h1>
          <p className="login-subtitle">Secure System Access</p>
        </div>

        <form onSubmit={handleLogin} className="login-form" noValidate>
          {error && (
            <div className={`error-message ${error.includes('already exists') || error.includes('successfully') ? 'success-message' : ''}`} role="alert">
              {error}
            </div>
          )}

          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <input
              type="email"
              id="email"
              name="email"
              value={loginData.email}
              onChange={handleInputChange}
              placeholder="admin@ptc.edu.ph"
              disabled={loading}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={loginData.password}
              onChange={handleInputChange}
              placeholder="Enter your password"
              disabled={loading}
              required
            />
          </div>

          <button type="submit" className="login-button" disabled={loading}>
            {loading ? (
              <>
                <div className="loading-spinner-small"></div>
                Signing In...
              </>
            ) : (
              'Sign In to Dashboard'
            )}
          </button>

          <div className="button-group">
            <button 
              type="button" 
              onClick={handleDemoLogin} 
              disabled={loading}
              className="secondary-button"
            >
              Use Demo Credentials
            </button>

            <button 
              type="button" 
              onClick={createDefaultAdmin} 
              disabled={loading}
              className="success-button"
            >
              Create Admin Account
            </button>
          </div>

          <button 
            type="button" 
            onClick={checkTableStructure}
            disabled={loading}
            className="tertiary-button"
          >
            Verify Database Structure
          </button>
        </form>

        <div className="login-footer">
          <p>Restricted Access - Authorized Personnel Only</p>
          <p className="footer-note">
            First time setup? Run the SQL script, then click "Create Admin Account"
          </p>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
