import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';
import './AdminDashboard.css';

const AdminDashboard = ({ onLogout }) => {
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedApplication, setSelectedApplication] = useState(null);
  const [showScheduleModal, setShowScheduleModal] = useState(false);
  const [examSchedule, setExamSchedule] = useState('');
  const [examTime, setExamTime] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [activeSection, setActiveSection] = useState('applications'); // New state for active nav item
  const [isSidebarOpen, setIsSidebarOpen] = useState(true); // New state for sidebar

  useEffect(() => {
    console.log('ðŸ”„ AdminDashboard mounted - starting fetchApplications');
    fetchApplications();
  }, []);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };  

  const fetchApplications = async () => {
    try {
      setLoading(true);
      console.log('ðŸ” Debug: Starting fetchApplications');
      console.log('ðŸ” Debug: Supabase URL:', process.env.REACT_APP_SUPABASE_URL);
      console.log('ðŸ” Debug: Supabase Key exists:', !!process.env.REACT_APP_SUPABASE_ANON_KEY);
      console.log('ðŸ” Debug: supabase client:', supabase);
      
      const { data, error } = await supabase
        .from('student_admissions')
        .select('*')
        .order('submitted_at', { ascending: false });

      console.log('ðŸ” Debug: Supabase response - error:', error);
      console.log('ðŸ” Debug: Supabase response - data length:', data?.length);
      console.log('ðŸ” Debug: Full error details:', JSON.stringify(error, null, 2));

      if (error) {
        console.log('ðŸ” Debug: Supabase error details:', error);
        throw error;
      }

      console.log('âœ… Debug: Successfully fetched applications:', data);
      setApplications(data || []);
    } catch (error) {
      console.error('ðŸ” Debug: Catch block error:', error);
      console.error('ðŸ” Debug: Error message:', error.message);
      console.error('ðŸ” Debug: Error code:', error.code);
      alert('Error loading applications: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleScheduleExam = (application) => {
    setSelectedApplication(application);
    
    // Set default schedule (tomorrow 9:00 AM)
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    const defaultDate = tomorrow.toISOString().split('T')[0];
    
    setExamSchedule(application.exam_schedule ? application.exam_schedule.split('T')[0] : defaultDate);
    setExamTime(application.exam_schedule ? application.exam_schedule.split('T')[1].substring(0, 5) : '09:00');
    setShowScheduleModal(true);
  };

  const submitExamSchedule = async () => {
    if (!examSchedule || !examTime) {
      alert('Please select both date and time');
      return;
    }

    try {
      const examDateTime = `${examSchedule}T${examTime}:00`;
      
      console.log('ðŸ” Debug: Scheduling exam for:', selectedApplication.id, 'at:', examDateTime);
      
      const { error } = await supabase
        .from('student_admissions')
        .update({ 
          exam_schedule: examDateTime,
          status: 'scheduled'
        })
        .eq('id', selectedApplication.id);

      if (error) throw error;

      // Send email notification
      await sendExamScheduleEmail(selectedApplication, examDateTime);

      alert('Exam scheduled successfully! Email sent to student.');
      setShowScheduleModal(false);
      fetchApplications(); // Refresh the list
    } catch (error) {
      console.error('Error scheduling exam:', error);
      alert('Error scheduling exam: ' + error.message);
    }
  };

  const sendExamScheduleEmail = async (application, examDateTime) => {
    try {
      const studentName = `${application.first_name} ${application.middle_name ? application.middle_name + ' ' : ''}${application.last_name}`;
      
      const response = await fetch('http://localhost:4001/send-confirmation', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          student_name: studentName,
          student_email: application.email,
          exam_schedule: examDateTime,
          year_level: application.year_level
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to send email');
      }

      console.log('Exam schedule email sent successfully');
    } catch (error) {
      console.error('Error sending email:', error);
      // Don't throw error here - the schedule was still saved
    }
  };

  const updateApplicationStatus = async (applicationId, status) => {
    try {
      const { error } = await supabase
        .from('student_admissions')
        .update({ status })
        .eq('id', applicationId);

      if (error) throw error;

      alert('Status updated successfully!');
      fetchApplications(); // Refresh the list
    } catch (error) {
      console.error('Error updating status:', error);
      alert('Error updating status: ' + error.message);
    }
  };

  const deleteApplication = async (applicationId) => {
    if (!window.confirm('Are you sure you want to delete this application?')) {
      return;
    }

    try {
      const { error } = await supabase
        .from('student_admissions')
        .delete()
        .eq('id', applicationId);

      if (error) throw error;

      alert('Application deleted successfully!');
      fetchApplications(); // Refresh the list
    } catch (error) {
      console.error('Error deleting application:', error);
      alert('Error deleting application: ' + error.message);
    }
  };

  // Filter applications based on status and search term
  const filteredApplications = applications.filter(app => {
    const matchesStatus = filterStatus === 'all' || app.status === filterStatus;
    const matchesSearch = 
      app.first_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      app.last_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      app.email?.toLowerCase().includes(searchTerm.toLowerCase());
    
    return matchesStatus && matchesSearch;
  });

  const getStatusBadge = (status) => {
    const statusConfig = {
      submitted: { label: 'Submitted', class: 'status-submitted' },
      scheduled: { label: 'Scheduled', class: 'status-scheduled' },
      completed: { label: 'Completed', class: 'status-completed' },
      rejected: { label: 'Rejected', class: 'status-rejected' }
    };
    
    const config = statusConfig[status] || { label: 'Pending', class: 'status-pending' };
    return <span className={`status-badge ${config.class}`}>{config.label}</span>;
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

const navItems = [
  { id: 'dashboard', label: 'Dashboard', icon: 'â£' },
  { id: 'applications', label: 'Applications', icon: 'ðŸ—‹' },
  { id: 'students', label: 'Students', icon: 'ðŸ‘¥' },
  { id: 'exams', label: 'Exams', icon: 'âœ“' },
  { id: 'reports', label: 'Reports', icon: 'â–¦' },
  { id: 'settings', label: 'Settings', icon: 'â›­' }
];

  if (loading) {
    return (
      <div className="admin-dashboard-loading">
        <div className="loading-spinner"></div>
        <p>Loading applications...</p>
      </div>
    );
  }

return (
    <div className="admin-dashboard">
 {/* Navigation Sidebar */}
<div className={`dashboard-sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
  <div className="sidebar-header">
    <div className="sidebar-logo">
      <img src="logo-ptc.png" alt="PTC Logo" />
    </div>
    {isSidebarOpen && (
      <div className="sidebar-title">
        <h3>PTC Admin</h3>
        <span className="admin-subtitle">Administration</span>
      </div>
    )}
  </div>
  
  <nav className="sidebar-nav">
    {navItems.map(item => (
      <button
        key={item.id}
        className={`nav-item ${activeSection === item.id ? 'active' : ''}`}
        onClick={() => setActiveSection(item.id)}
        title={item.label}
      >
        <span className="nav-icon">{item.icon}</span>
        {isSidebarOpen && (
          <div className="nav-content">
            <span className="nav-label">{item.label}</span>
            <span className="nav-arrow">â€º</span>
          </div>
        )}
      </button>
    ))}
  </nav>
  
  <div className="sidebar-footer">
    <button className="nav-item logout-nav" onClick={onLogout} title="Logout">
      <span className="nav-icon">â†’</span>
      {isSidebarOpen && (
        <div className="nav-content">
          <span className="nav-label">Logout</span>
        </div>
      )}
    </button>
  </div>

  {/* Toggle Button */}
  <button className="sidebar-toggle" onClick={toggleSidebar}>
    <span className="toggle-icon">
      {isSidebarOpen ? 'â€¹' : 'â€º'}
    </span>
  </button>
</div>

      {/* Main Content Area */}
      <div className="dashboard-main">
        {/* Dashboard Header */}
        <div className="dashboard-header">
          <div className="header-content">
            <div className="header-left">
              <button className="mobile-menu-btn" onClick={toggleSidebar}>
                <span className="menu-icon">â˜°</span>
              </button>
              <h1 className="page-title">
                {navItems.find(item => item.id === activeSection)?.label || 'Dashboard'}
              </h1>
            </div>
            <div className="admin-info">
              <div className="admin-welcome">Welcome, Administrator</div>
            </div>
          </div>
        </div>
        
        {/* Main Content */}
        <div className="dashboard-content">
          {activeSection === 'applications' && (
            <>
              {/* Statistics Cards */}
              <div className="stats-container">
                <div className="stat-card">
                  <h3>Total Applications</h3>
                  <div className="stat-number">{applications.length}</div>
                </div>
                <div className="stat-card">
                  <h3>Scheduled Exams</h3>
                  <div className="stat-number">
                    {applications.filter(app => app.status === 'scheduled').length}
                  </div>
                </div>
                <div className="stat-card">
                  <h3>Pending Review</h3>
                  <div className="stat-number">
                    {applications.filter(app => !app.status || app.status === 'submitted').length}
                  </div>
                </div>
              </div>

              {/* Filters and Search */}
              <div className="dashboard-controls">
                <div className="search-box">
                  <input
                    type="text"
                    placeholder="Search by name or email..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                  <span className="search-icon">ðŸ”</span>
                </div>
                
                <div className="filter-controls">
                  <select 
                    value={filterStatus} 
                    onChange={(e) => setFilterStatus(e.target.value)}
                  >
                    <option value="all">All Status</option>
                    <option value="submitted">Submitted</option>
                    <option value="scheduled">Scheduled</option>
                    <option value="completed">Completed</option>
                    <option value="rejected">Rejected</option>
                  </select>
                  
                  <button onClick={fetchApplications} className="refresh-btn">
                    ðŸ”„ Refresh
                  </button>
                </div>
              </div>

              {/* Applications Table */}
              <div className="applications-table-container">
                <table className="applications-table">
                  <thead>
                    <tr>
                      <th>Student Name</th>
                      <th>Email</th>
                      <th>Program</th>
                      <th>Year Level</th>
                      <th>Submitted Date</th>
                      <th>Exam Schedule</th>
                      <th>Status</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredApplications.length === 0 ? (
                      <tr>
                        <td colSpan="8" className="no-data">
                          No applications found
                        </td>
                      </tr>
                    ) : (
                      filteredApplications.map((application) => (
                        <tr key={application.id}>
                          <td>
                            <strong>{application.first_name} {application.last_name}</strong>
                          </td>
                          <td>{application.email}</td>
                          <td>{application.desired_program}</td>
                          <td>{application.year_level}</td>
                          <td>{formatDate(application.submitted_at)}</td>
                          <td>
                            {application.exam_schedule ? (
                              formatDate(application.exam_schedule)
                            ) : (
                              <span className="not-scheduled">Not Scheduled</span>
                            )}
                          </td>
                          <td>{getStatusBadge(application.status)}</td>
                          <td>
                            <div className="action-buttons">
                              <button
                                onClick={() => handleScheduleExam(application)}
                                className="btn-schedule"
                                title="Schedule Exam"
                              >
                                ðŸ—“ï¸ Schedule
                              </button>
                              
                              <select
                                value={application.status || 'submitted'}
                                onChange={(e) => updateApplicationStatus(application.id, e.target.value)}
                                className="status-select"
                              >
                                <option value="submitted">Submitted</option>
                                <option value="scheduled">Scheduled</option>
                                <option value="completed">Completed</option>
                                <option value="rejected">Rejected</option>
                              </select>
                              
                              <button
                                onClick={() => deleteApplication(application.id)}
                                className="btn-delete"
                                title="Delete Application"
                              >
                                ðŸ—‘ï¸
                              </button>
                            </div>
                          </td>
                        </tr>
                      ))
                    )}
                  </tbody>
                </table>
              </div>
            </>
          )}

          {/* Placeholder for other sections */}
          {activeSection !== 'applications' && (
            <div className="section-placeholder">
              <h2>{navItems.find(item => item.id === activeSection)?.label} Section</h2>
              <p>This section is under development. The {activeSection} functionality will be implemented here.</p>
            </div>
          )}
        </div>
      </div>

      {/* Schedule Exam Modal */}
      {showScheduleModal && selectedApplication && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2>Schedule Exam</h2>
              <button 
                onClick={() => setShowScheduleModal(false)}
                className="close-btn"
              >
                Ã—
              </button>
            </div>
            
            <div className="modal-body">
              <div className="student-info">
                <h4>Student: {selectedApplication.first_name} {selectedApplication.last_name}</h4>
                <p>Email: {selectedApplication.email}</p>
                <p>Program: {selectedApplication.desired_program}</p>
              </div>
              
              <div className="schedule-form">
                <div className="form-group">
                  <label>Exam Date *</label>
                  <input
                    type="date"
                    value={examSchedule}
                    onChange={(e) => setExamSchedule(e.target.value)}
                    min={new Date().toISOString().split('T')[0]}
                  />
                </div>
                
                <div className="form-group">
                  <label>Exam Time *</label>
                  <input
                    type="time"
                    value={examTime}
                    onChange={(e) => setExamTime(e.target.value)}
                  />
                </div>
              </div>
            </div>
            
            <div className="modal-footer">
              <button 
                onClick={() => setShowScheduleModal(false)}
                className="btn-cancel"
              >
                Cancel
              </button>
              <button 
                onClick={submitExamSchedule}
                className="btn-confirm"
              >
                ðŸ—“ï¸ Schedule Exam & Send Email
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
