// src/components/AdminLogin.jsx
import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';
import './AdminLogin.css';

const AdminLogin = ({ onLoginSuccess }) => {
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isFocused, setIsFocused] = useState({ email: false, password: false });

  // Subtle geometric background animation
  useEffect(() => {
    const canvas = document.getElementById('bgCanvas');
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    let animationFrameId;

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };
    
    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    const colors = ['#f8fafc', '#f1f5f9', '#e2e8f0'];
    const shapes = [];

    class GeometricShape {
      constructor() {
        this.type = Math.floor(Math.random() * 3);
        this.size = Math.random() * 40 + 20;
        this.x = Math.random() * canvas.width;
        this.y = Math.random() * canvas.height;
        this.color = colors[Math.floor(Math.random() * colors.length)];
        this.speed = Math.random() * 0.5 + 0.1;
        this.direction = Math.random() * Math.PI * 2;
        this.rotation = 0;
        this.rotationSpeed = (Math.random() - 0.5) * 0.02;
      }

      update() {
        this.x += Math.cos(this.direction) * this.speed;
        this.y += Math.sin(this.direction) * this.speed;
        this.rotation += this.rotationSpeed;

        // Bounce off edges
        if (this.x < -this.size || this.x > canvas.width + this.size) {
          this.direction = Math.PI - this.direction;
        }
        if (this.y < -this.size || this.y > canvas.height + this.size) {
          this.direction = -this.direction;
        }
      }

      draw() {
        ctx.save();
        ctx.translate(this.x, this.y);
        ctx.rotate(this.rotation);
        ctx.fillStyle = this.color;
        ctx.globalAlpha = 0.4;

        switch (this.type) {
          case 0: // Triangle
            ctx.beginPath();
            ctx.moveTo(0, -this.size);
            ctx.lineTo(this.size * 0.866, this.size * 0.5);
            ctx.lineTo(-this.size * 0.866, this.size * 0.5);
            ctx.closePath();
            ctx.fill();
            break;
          case 1: // Square
            ctx.fillRect(-this.size/2, -this.size/2, this.size, this.size);
            break;
          case 2: // Circle
            ctx.beginPath();
            ctx.arc(0, 0, this.size/2, 0, Math.PI * 2);
            ctx.fill();
            break;
        }

        ctx.restore();
      }
    }

    // Create shapes
    for (let i = 0; i < 8; i++) {
      shapes.push(new GeometricShape());
    }

    const animate = () => {
      ctx.fillStyle = '#ffffff';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      shapes.forEach(shape => {
        shape.update();
        shape.draw();
      });

      animationFrameId = requestAnimationFrame(animate);
    };

    animate();

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      cancelAnimationFrame(animationFrameId);
    };
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoginData(prev => ({ ...prev, [name]: value }));
  };

  const handleFocus = (field) => () => {
    setIsFocused(prev => ({ ...prev, [field]: true }));
  };

  const handleBlur = (field) => () => {
    setIsFocused(prev => ({ ...prev, [field]: false }));
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!loginData.email || !loginData.password) {
      setError('Please fill in all fields');
      return;
    }

    try {
      setLoading(true);
      setError('');

      const { data: adminRow, error: adminError } = await supabase
        .from('admin_users')
        .select('*')
        .eq('email', loginData.email)
        .eq('password', loginData.password)
        .single();

      if (adminError) {
        if (adminError.code === 'PGRST116') {
          throw new Error('Invalid email or password');
        }
        throw adminError;
      }

      if (!adminRow) {
        throw new Error('Invalid email or password');
      }

      const user = {
        id: adminRow.id,
        email: adminRow.email,
        user_metadata: {
          full_name: adminRow.full_name
        }
      };

      const admin = {
        id: adminRow.id,
        full_name: adminRow.full_name,
        role: adminRow.role,
        email: adminRow.email
      };

      onLoginSuccess && onLoginSuccess({ user, admin });

    } catch (err) {
      setError(err.message || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  const createDefaultAdmin = async () => {
    try {
      setLoading(true);
      setError('');

      const { data: existingAdmin } = await supabase
        .from('admin_users')
        .select('id')
        .eq('email', 'admin@ptc.edu.ph')
        .single();

      if (existingAdmin) {
        setError('Admin user already exists. Use: admin@ptc.edu.ph / demo123');
        setLoginData({ email: 'admin@ptc.edu.ph', password: 'demo123' });
        return;
      }

      const { error: insertError } = await supabase
        .from('admin_users')
        .insert([
          {
            email: 'admin@ptc.edu.ph',
            password: 'demo123',
            full_name: 'PTC Administrator',
            role: 'super_admin'
          }
        ]);

      if (insertError) throw insertError;

      setError('Default admin created! Use: admin@ptc.edu.ph / demo123');
      setLoginData({ email: 'admin@ptc.edu.ph', password: 'demo123' });

    } catch (error) {
      setError('Failed to create admin: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDemoLogin = () => {
    setLoginData({ email: 'admin@ptc.edu.ph', password: 'demo123' });
    setError('');
  };

  return (
    <div className="login-container">
      <canvas id="bgCanvas" className="background-canvas" />
      
      <div className="login-layout">
        {/* Brand Section */}
        <div className="brand-section">
          <div className="brand-logo">
            <img src="/logo-ptc.png" alt="PTC Logo" className="logo-image" />
          </div>
          <div className="brand-text">
            <h1 className="college-name">Pateros Technological College</h1>
            <p className="college-tagline">Excellence in Technological Education</p>
          </div>
        </div>

        {/* Login Card */}
        <div className="login-card">
          <div className="card-header">
            <div className="card-icon">
              <div className="icon-shield"></div>
            </div>
            <h2 className="card-title">Administrator Access</h2>
            <p className="card-subtitle">Secure system authentication</p>
          </div>

          <form onSubmit={handleLogin} className="login-form">
            {error && (
              <div className={`alert-message ${error.includes('already exists') || error.includes('created') ? 'alert-success' : 'alert-error'}`}>
                {error}
              </div>
            )}

            <div className="input-group">
              <div className={`input-container ${isFocused.email ? 'input-focused' : ''}`}>
                <input
                  type="email"
                  name="email"
                  value={loginData.email}
                  onChange={handleInputChange}
                  onFocus={handleFocus('email')}
                  onBlur={handleBlur('email')}
                  placeholder=" "
                  disabled={loading}
                  className="form-input"
                />
                <label className="input-label">Admin Email</label>
                <div className="input-underline"></div>
              </div>
            </div>

            <div className="input-group">
              <div className={`input-container ${isFocused.password ? 'input-focused' : ''}`}>
                <input
                  type="password"
                  name="password"
                  value={loginData.password}
                  onChange={handleInputChange}
                  onFocus={handleFocus('password')}
                  onBlur={handleBlur('password')}
                  placeholder=" "
                  disabled={loading}
                  className="form-input"
                />
                <label className="input-label">Password</label>
                <div className="input-underline"></div>
              </div>
            </div>

            <button 
              type="submit" 
              className={`login-button ${loading ? 'button-loading' : ''}`}
              disabled={loading}
            >
              {loading ? (
                <>
                  <span className="button-spinner"></span>
                  Authenticating...
                </>
              ) : (
                'Access Dashboard'
              )}
            </button>

            <div className="action-buttons">
              <button 
                type="button" 
                onClick={handleDemoLogin}
                className="action-button secondary"
                disabled={loading}
              >
                Use Demo Credentials
              </button>
              
              <button 
                type="button" 
                onClick={createDefaultAdmin}
                className="action-button primary"
                disabled={loading}
              >
                Initialize Admin Account
              </button>
            </div>
          </form>

          <div className="card-footer">
            <p className="security-notice">Restricted Access â€¢ Authorized Personnel Only</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
