// src/components/AdminLogin.jsx
import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import './AdminLogin.css';

const AdminLogin = ({ onLoginSuccess }) => {
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoginData(prev => ({ ...prev, [name]: value }));
  };

  // STEP 1: CREATE USER DIRECTLY IN SUPABASE AUTH
  const createAdminDirectly = async () => {
    try {
      setLoading(true);
      setError('');
      setSuccess('');

      // Use a simple test email that Supabase will accept
      const testEmail = `admin${Date.now()}@gmail.com`;
      
      console.log('Creating user with email:', testEmail);

      const { data: authData, error: authError } = await supabase.auth.signUp({
        email: testEmail,
        password: 'demo123',
      });

      if (authError) {
        console.error('Signup error:', authError);
        throw new Error(`Signup failed: ${authError.message}`);
      }

      if (authData.user) {
        console.log('User created successfully:', authData.user);
        
        // STEP 2: ADD TO ADMIN_USERS TABLE
        const { error: adminError } = await supabase
          .from('admin_users')
          .insert([
            {
              id: authData.user.id,
              email: testEmail,
              full_name: 'PTC Administrator',
              role: 'super_admin'
            }
          ]);

        if (adminError && !adminError.message.includes('duplicate key')) {
          console.warn('Admin table insert warning:', adminError);
        }

        setSuccess(`‚úÖ Admin account created! Email: ${testEmail}`);
        setLoginData({ email: testEmail, password: 'demo123' });
        
        // Auto-login after creation
        setTimeout(() => {
          handleAutoLogin(testEmail, 'demo123');
        }, 1000);
      }

    } catch (error) {
      console.error('Create admin error:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  // AUTO LOGIN AFTER CREATION
  const handleAutoLogin = async (email, password) => {
    try {
      setLoading(true);
      
      const { data: authData, error: authError } = await supabase.auth.signInWithPassword({
        email: email,
        password: password,
      });

      if (authError) throw authError;

      const user = authData?.user;
      if (!user) throw new Error('Auto-login failed');

      // Check admin table
      const { data: adminRow } = await supabase
        .from('admin_users')
        .select('id, full_name, role, email')
        .eq('id', user.id)
        .single();

      if (!adminRow) {
        throw new Error('Admin record not found');
      }

      onLoginSuccess && onLoginSuccess({ user, admin: adminRow });

    } catch (err) {
      setError('Auto-login failed: ' + err.message);
      setLoading(false);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!loginData.email || !loginData.password) {
      setError('Please fill in all fields');
      return;
    }

    try {
      setLoading(true);
      setError('');
      setSuccess('');

      const { data: authData, error: authError } = await supabase.auth.signInWithPassword({
        email: loginData.email,
        password: loginData.password,
      });

      if (authError) {
        // If login fails, suggest creating account
        if (authError.message.includes('Invalid login credentials')) {
          throw new Error('Account does not exist. Click "Create Admin Account" first.');
        }
        throw authError;
      }

      const user = authData?.user;
      if (!user) throw new Error('Authentication failed.');

      // Check admin table
      const { data: adminRow, error: adminError } = await supabase
        .from('admin_users')
        .select('id, full_name, role, email')
        .eq('id', user.id)
        .single();

      if (adminError || !adminRow) {
        await supabase.auth.signOut();
        throw new Error('Access denied. Admin account required.');
      }

      onLoginSuccess && onLoginSuccess({ user, admin: adminRow });

    } catch (err) {
      console.error('Login error:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDemoLogin = () => {
    setLoginData({ email: '', password: '' });
    setError('');
    setSuccess('Click "Create Admin Account" first to generate credentials');
  };

  // TEST CONNECTION
  const testConnection = async () => {
    const { data, error } = await supabase.auth.getSession();
    console.log('Connection test:', { data, error });
    alert(error ? `Connection failed: ${error.message}` : 'Connection OK!');
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <div className="logo-container">
            <img src="/logo-ptc.png" alt="PTC Logo" className="logo-image" />
          </div>
          <div className="college-info">
            <h1 className="college-name">Pateros Technological College</h1>
            <p className="login-title">Admin Portal</p>
          </div>
        </div>

        <form onSubmit={handleLogin} className="login-form" noValidate>
          {error && (
            <div className="error-message" role="alert">
              ‚ö†Ô∏è {error}
            </div>
          )}
          
          {success && (
            <div className="success-message" role="alert" style={{background: '#d4edda', color: '#155724', padding: '10px', borderRadius: '4px', marginBottom: '10px'}}>
              ‚úÖ {success}
            </div>
          )}

          <div className="form-group">
            <label htmlFor="email">Admin Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={loginData.email}
              onChange={handleInputChange}
              placeholder="Will be auto-filled after creation"
              disabled={loading}
              required
              autoComplete="username"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={loginData.password}
              onChange={handleInputChange}
              placeholder="demo123"
              disabled={loading}
              required
              autoComplete="current-password"
            />
          </div>

          <button type="submit" className="login-button" disabled={loading || !loginData.email}>
            {loading ? (
              <>
                <div className="loading-spinner-small" aria-hidden="true"></div>
                Signing In...
              </>
            ) : (
              'üîê Sign In'
            )}
          </button>

          {/* MAIN ACTION BUTTON */}
          <button 
            type="button" 
            className="create-admin-button" 
            onClick={createAdminDirectly} 
            disabled={loading}
            style={{
              background: '#28a745',
              width: '100%',
              padding: '12px',
              fontSize: '16px',
              fontWeight: 'bold',
              marginTop: '10px'
            }}
          >
            üöÄ CREATE ADMIN ACCOUNT (Click This First)
          </button>

          <div style={{display: 'flex', gap: '10px', marginTop: '10px'}}>
            <button type="button" className="demo-login-button" onClick={handleDemoLogin} disabled={loading}>
              ‚ÑπÔ∏è Get Instructions
            </button>
            
            <button type="button" onClick={testConnection} disabled={loading}
              style={{background: '#6c757d', fontSize: '12px', padding: '8px'}}>
              üîß Test Connection
            </button>
          </div>
        </form>

        <div className="login-footer">
          <p>Restricted Access - Authorized Personnel Only</p>
          <p style={{ fontSize: '12px', color: '#666', marginTop: '5px' }}>
            Step 1: Click "CREATE ADMIN ACCOUNT"<br/>
            Step 2: Wait for success message<br/>
            Step 3: Click "Sign In"
          </p>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
