// Add this to your login component or run in browser console
async function testConnection() {
  const { data, error } = await supabase.auth.getSession()
  if (error) {
    console.error('Connection test failed:', error)
  } else {
    console.log('Connection successful:', data)
  }
}
testConnection()

// src/components/AdminLogin.jsx
import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import './AdminLogin.css'; // keep your css here

const AdminLogin = ({ onLoginSuccess }) => {
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoginData(prev => ({ ...prev, [name]: value }));
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!loginData.email || !loginData.password) {
      setError('Please fill in all fields');
      return;
    }

    try {
      setLoading(true);
      setError('');

      // 1) Sign in using Supabase Auth
      const { data: authData, error: authError } = await supabase.auth.signInWithPassword({
        email: loginData.email,
        password: loginData.password,
      });

      if (authError) throw authError;
      const user = authData?.user;
      if (!user) throw new Error('Authentication failed.');

      // 2) Check admin_users table for this user id
      const { data: adminRow, error: adminError } = await supabase
        .from('admin_users')
        .select('id, full_name, role, email')
        .eq('id', user.id)
        .single();

      if (adminError || !adminRow) {
        // not an admin -> sign out and deny
        await supabase.auth.signOut();
        throw new Error('Access denied. Admin account required.');
      }

      // SUCCESS
      onLoginSuccess && onLoginSuccess({ user, admin: adminRow });

    } catch (err) {
      console.error('Login error:', err);
      // Show friendly message
      setError(err.message || 'Invalid email or password');
    } finally {
      setLoading(false);
    }
  };

  // Demo fill ‚Äî remove in production
  const handleDemoLogin = () => {
    setLoginData({ email: 'admin@ptc.edu.ph', password: 'demo123' });
    setError('');
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <div className="logo-container">
            <img src="/logo-ptc.png" alt="PTC Logo" className="logo-image" />
          </div>
          <div className="college-info">
            <h1 className="college-name">Pateros Technological College</h1>
            <p className="login-title">Admin Portal</p>
          </div>
        </div>

        <form onSubmit={handleLogin} className="login-form" noValidate>
          {error && <div className="error-message" role="alert">‚ö†Ô∏è {error}</div>}

          <div className="form-group">
            <label htmlFor="email">Admin Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={loginData.email}
              onChange={handleInputChange}
              placeholder="admin@ptc.edu.ph"
              disabled={loading}
              required
              autoComplete="username"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={loginData.password}
              onChange={handleInputChange}
              placeholder="Enter your password"
              disabled={loading}
              required
              autoComplete="current-password"
            />
          </div>

          <button type="submit" className="login-button" disabled={loading}>
            {loading ? (
              <>
                <div className="loading-spinner-small" aria-hidden="true"></div>
                Signing In...
              </>
            ) : (
              'üîê Sign In'
            )}
          </button>

          <button type="button" className="demo-login-button" onClick={handleDemoLogin} disabled={loading}>
            üöÄ Fill Demo Credentials
          </button>
        </form>

        <div className="login-footer">
          <p>Restricted Access - Authorized Personnel Only</p>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
