import React, { useState } from 'react';
import './AdminDashboard.css'; // reuse styles

const subjectsMock = [
  {
    id: 1,
    code: 'IT101',
    name: 'Introduction to IT',
    section: 'A',
    availableSlot: 5,
    status: 'Regular', // only for Regular students
    units: 3
  },
  {
    id: 2,
    code: 'IT102',
    name: 'Computer Programming 1',
    section: 'B',
    availableSlot: 2,
    status: 'Regular',
    units: 3
  },
  {
    id: 3,
    code: 'IT201',
    name: 'Data Structures',
    section: 'C',
    availableSlot: 10,
    status: 'Irregular',
    units: 3
  },
  {
    id: 4,
    code: 'GE101',
    name: 'Purposive Communication',
    section: 'D',
    availableSlot: 0,
    status: 'Regular',
    units: 3
  },
  {
    id: 5,
    code: 'PE101',
    name: 'Physical Education 1',
    section: 'E',
    availableSlot: 8,
    status: 'Irregular',
    units: 2
  }
];

const GradeAdvisingSection = () => {
  const [studentStatus, setStudentStatus] = useState('Regular'); // Regular / Irregular
  const [selectedSubjects, setSelectedSubjects] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [activeTab, setActiveTab] = useState('advising'); // advising | drop | transferee

  const totalUnits = selectedSubjects.reduce((sum, s) => sum + s.units, 0);

  const canTakeSubject = (subject) => {
    // If student is Irregular, cannot take subjects for Regular only
    if (studentStatus === 'Irregular' && subject.status === 'Regular') {
      return false;
    }

    // No available slot
    if (subject.availableSlot <= 0) return false;

    // Already selected
    if (selectedSubjects.some((s) => s.id === subject.id)) return false;

    return true;
  };

  const handleAddSubject = (subject) => {
    if (!canTakeSubject(subject)) {
      if (studentStatus === 'Irregular' && subject.status === 'Regular') {
        setErrorMessage(
          `You are marked as IRREGULAR. You cannot enroll in "${subject.code} - ${subject.name}" (Regular only).`
        );
      } else if (subject.availableSlot <= 0) {
        setErrorMessage(`No available slot for "${subject.code} - ${subject.name}".`);
      } else {
        setErrorMessage('You cannot add this subject.');
      }
      return;
    }

    setErrorMessage('');
    setSelectedSubjects((prev) => [...prev, subject]);
  };

  const handleRemoveSubject = (subjectId) => {
    setSelectedSubjects((prev) => prev.filter((s) => s.id !== subjectId));
  };

  // --- Tab Content ---

  const renderAdvisingTab = () => (
    <>
      <div className="grade-advising-header">
        <h2>Grade Advising</h2>

        <div className="grade-advising-status-select">
          <label htmlFor="studentStatus">Student Status:</label>
          <select
            id="studentStatus"
            value={studentStatus}
            onChange={(e) => setStudentStatus(e.target.value)}
          >
            <option value="Regular">Regular</option>
            <option value="Irregular">Irregular</option>
          </select>
        </div>
      </div>

      {errorMessage && <div className="error-banner">{errorMessage}</div>}

      <h3 className="section-title">Subjects to be Taken</h3>

      <div className="table-wrapper">
        <table className="subjects-table">
          <thead>
            <tr>
              <th>Subject Code</th>
              <th>Subject Name</th>
              <th>Section</th>
              <th>Available Slot</th>
              <th>Status (Regular / Irregular)</th>
              <th>Units</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {subjectsMock.map((subject) => {
              const disabled = !canTakeSubject(subject);
              return (
                <tr key={subject.id} className={disabled ? 'row-disabled' : ''}>
                  <td>{subject.code}</td>
                  <td>{subject.name}</td>
                  <td>{subject.section}</td>
                  <td>{subject.availableSlot}</td>
                  <td>{subject.status}</td>
                  <td>{subject.units}</td>
                  <td>
                    <button
                      className="btn primary"
                      disabled={disabled}
                      onClick={() => handleAddSubject(subject)}
                    >
                      {disabled ? 'Not Allowed' : 'Add'}
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      <h3 className="section-title">Selected Subjects</h3>

      {selectedSubjects.length === 0 ? (
        <p className="empty-state">No subjects selected yet.</p>
      ) : (
        <div className="table-wrapper">
          <table className="subjects-table">
            <thead>
              <tr>
                <th>Subject Code</th>
                <th>Subject Name</th>
                <th>Section</th>
                <th>Units</th>
                <th>Remove</th>
              </tr>
            </thead>
            <tbody>
              {selectedSubjects.map((subject) => (
                <tr key={subject.id}>
                  <td>{subject.code}</td>
                  <td>{subject.name}</td>
                  <td>{subject.section}</td>
                  <td>{subject.units}</td>
                  <td>
                    <button
                      className="btn danger"
                      onClick={() => handleRemoveSubject(subject.id)}
                    >
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
              <tr className="total-row">
                <td colSpan={3} style={{ textAlign: 'right', fontWeight: 'bold' }}>
                  Total Units:
                </td>
                <td style={{ fontWeight: 'bold' }}>{totalUnits}</td>
                <td />
              </tr>
            </tbody>
          </table>
        </div>
      )}
    </>
  );

  const renderDropTab = () => (
    <div className="grade-advising-subpage">
      <h2>Drop Student</h2>
      <p className="placeholder-text">
        Separate navigation for dropped students.  
        You can add your form here (select student, subjects to drop, reason, etc.).
      </p>
    </div>
  );

  const renderTransfereeTab = () => (
    <div className="grade-advising-subpage">
      <h2>Transferee</h2>
      <p className="placeholder-text">
        Separate navigation for transferees.  
        Add fields for credited subjects, evaluation, and recommended subjects here.
      </p>
    </div>
  );

  let tabContent;
  if (activeTab === 'advising') tabContent = renderAdvisingTab();
  else if (activeTab === 'drop') tabContent = renderDropTab();
  else tabContent = renderTransfereeTab();

  return (
    <div className="dashboard-card grade-advising-container">
      {/* Tabs for Grade Advising / Drop / Transferee */}
      <div className="grade-advising-tabs">
        <button
          className={`grade-tab ${activeTab === 'advising' ? 'active' : ''}`}
          onClick={() => setActiveTab('advising')}
        >
          Grade Advising
        </button>
        <button
          className={`grade-tab ${activeTab === 'drop' ? 'active' : ''}`}
          onClick={() => setActiveTab('drop')}
        >
          Drop Student
        </button>
        <button
          className={`grade-tab ${activeTab === 'transferee' ? 'active' : ''}`}
          onClick={() => setActiveTab('transferee')}
        >
          Transferee
        </button>
      </div>

      <div className="grade-advising-body">{tabContent}</div>
    </div>
  );
};

export default GradeAdvisingSection;
