// src/App.jsx
import React, { useEffect, useState } from 'react';
import { supabase } from './supabaseClient';
import AdminLogin from './components/AdminLogin';

// A minimal admin dashboard component
const AdminDashboard = ({ admin, onSignOut }) => (
  <div style={{ padding: 20 }}>
    <h2>Welcome, {admin?.full_name || admin?.email}</h2>
    <p>Role: {admin?.role || 'admin'}</p>
    <button onClick={onSignOut}>Sign Out</button>
  </div>
);

export default function App() {
  const [session, setSession] = useState(null);
  const [admin, setAdmin] = useState(null);
  const [loadingInit, setLoadingInit] = useState(true);

  useEffect(() => {
    let mounted = true;

    async function init() {
      // get current session (if any)
      const { data } = await supabase.auth.getSession();
      if (!mounted) return;
      setSession(data?.session ?? null);

      // if session exists, fetch admin row
      if (data?.session?.user) {
        const user = data.session.user;
        const { data: adminRow } = await supabase.from('admin_users').select('id, full_name, role, email').eq('id', user.id).single();
        setAdmin(adminRow ?? null);
      }

      setLoadingInit(false);
    }

    init();

    const { data: listener } = supabase.auth.onAuthStateChange((_event, newSession) => {
      setSession(newSession?.session ?? null);
      if (!newSession?.session) {
        setAdmin(null);
        return;
      }
      // fetch admin row when signed in
      (async () => {
        const user = newSession.session.user;
        const { data: adminRow } = await supabase.from('admin_users').select('id, full_name, role, email').eq('id', user.id).single();
        setAdmin(adminRow ?? null);
      })();
    });

    return () => {
      mounted = false;
      listener?.subscription?.unsubscribe();
    };
  }, []);

  const handleLoginSuccess = ({ user, admin: adminRow }) => {
    setSession({ user });
    setAdmin(adminRow);
  };

  const handleSignOut = async () => {
    await supabase.auth.signOut();
    setSession(null);
    setAdmin(null);
  };

  if (loadingInit) return <div style={{ padding: 20 }}>Loading...</div>;

  if (!session || !admin) {
    // show login when not authenticated or not admin
    return <AdminLogin onLoginSuccess={handleLoginSuccess} />;
  }

  return <AdminDashboard admin={admin} onSignOut={handleSignOut} />;
}
