import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import FileUpload from './FileUpload';
import './AdmissionForm.css';

const AdmissionForm = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [currentStep, setCurrentStep] = useState(1);
  const [formErrors, setFormErrors] = useState({});

  const [formData, setFormData] = useState({
    // Personal Information
    first_name: '',
    last_name: '',
    email: '',
    phone: '',
    birthdate: '',
    address: '',
    
    // Education
    high_school: '',
    program: '',
    student_type: 'new',
    
    // Documents
    id_photo: '',
    good_moral: '',
    report_card: '',
    diploma: ''
  });

  const programs = [
    'Computer Science',
    'Engineering',
    'Business Administration',
    'Education',
    'Nursing',
    'Psychology'
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user types
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleFileUpload = (fileType, fileUrl) => {
    setFormData(prev => ({
      ...prev,
      [fileType]: fileUrl
    }));
  };

  const validateStep = (step) => {
    const errors = {};

    if (step === 1) {
      if (!formData.first_name.trim()) errors.first_name = 'Required';
      if (!formData.last_name.trim()) errors.last_name = 'Required';
      if (!formData.email.trim()) errors.email = 'Required';
      else if (!/\S+@\S+\.\S+/.test(formData.email)) errors.email = 'Invalid email';
      if (!formData.phone.trim()) errors.phone = 'Required';
      if (!formData.birthdate) errors.birthdate = 'Required';
      if (!formData.address.trim()) errors.address = 'Required';
    }

    if (step === 2) {
      if (!formData.high_school.trim()) errors.high_school = 'Required';
      if (!formData.program) errors.program = 'Required';
    }

    if (step === 3) {
      if (!formData.id_photo) errors.id_photo = 'Required';
      if (!formData.good_moral) errors.good_moral = 'Required';
      if (!formData.report_card) errors.report_card = 'Required';
      if (!formData.diploma) errors.diploma = 'Required';
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const nextStep = () => {
    if (validateStep(currentStep)) {
      setCurrentStep(prev => prev + 1);
    }
  };

  const prevStep = () => {
    setCurrentStep(prev => prev - 1);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    if (!validateStep(3)) {
      setCurrentStep(3);
      setLoading(false);
      return;
    }

    try {
      const examDate = new Date();
      examDate.setDate(examDate.getDate() + 7);

      const { data, error } = await supabase
        .from('student_admissions')
        .insert([{
          ...formData,
          exam_date: examDate.toISOString(),
          submitted_at: new Date().toISOString()
        }])
        .select();

      if (error) throw error;

      // Send confirmation email (simplified)
      await fetch(`${process.env.REACT_APP_API_URL}/send-confirmation`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: `${formData.first_name} ${formData.last_name}`,
          email: formData.email,
          exam_date: examDate.toISOString()
        })
      });

      alert('Application submitted! Check your email for exam details.');
      navigate('/applications');

    } catch (error) {
      alert('Error: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="admission-page">
      <div className="form-card">
        {/* Header */}
        <div className="form-header">
          <h1>Student Application</h1>
          <p>Step {currentStep} of 3</p>
        </div>

        {/* Progress Bar */}
        <div className="progress-bar">
          <div 
            className="progress-fill" 
            style={{ width: `${(currentStep / 3) * 100}%` }}
          ></div>
        </div>

        <form onSubmit={handleSubmit}>
          {/* Step 1: Personal Info */}
          {currentStep === 1 && (
            <div className="form-step">
              <h2>Personal Information</h2>
              
              <div className="input-grid">
                <div className="input-group">
                  <label>First Name *</label>
                  <input
                    type="text"
                    name="first_name"
                    value={formData.first_name}
                    onChange={handleInputChange}
                    className={formErrors.first_name ? 'error' : ''}
                  />
                  {formErrors.first_name && <span className="error-text">{formErrors.first_name}</span>}
                </div>

                <div className="input-group">
                  <label>Last Name *</label>
                  <input
                    type="text"
                    name="last_name"
                    value={formData.last_name}
                    onChange={handleInputChange}
                    className={formErrors.last_name ? 'error' : ''}
                  />
                  {formErrors.last_name && <span className="error-text">{formErrors.last_name}</span>}
                </div>

                <div className="input-group">
                  <label>Email *</label>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    className={formErrors.email ? 'error' : ''}
                  />
                  {formErrors.email && <span className="error-text">{formErrors.email}</span>}
                </div>

                <div className="input-group">
                  <label>Phone *</label>
                  <input
                    type="tel"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    className={formErrors.phone ? 'error' : ''}
                  />
                  {formErrors.phone && <span className="error-text">{formErrors.phone}</span>}
                </div>

                <div className="input-group">
                  <label>Birthdate *</label>
                  <input
                    type="date"
                    name="birthdate"
                    value={formData.birthdate}
                    onChange={handleInputChange}
                    className={formErrors.birthdate ? 'error' : ''}
                  />
                  {formErrors.birthdate && <span className="error-text">{formErrors.birthdate}</span>}
                </div>

                <div className="input-group full-width">
                  <label>Address *</label>
                  <input
                    type="text"
                    name="address"
                    value={formData.address}
                    onChange={handleInputChange}
                    className={formErrors.address ? 'error' : ''}
                    placeholder="Street, City, State, ZIP"
                  />
                  {formErrors.address && <span className="error-text">{formErrors.address}</span>}
                </div>
              </div>
            </div>
          )}

          {/* Step 2: Education */}
          {currentStep === 2 && (
            <div className="form-step">
              <h2>Education Background</h2>
              
              <div className="input-grid">
                <div className="input-group full-width">
                  <label>High School *</label>
                  <input
                    type="text"
                    name="high_school"
                    value={formData.high_school}
                    onChange={handleInputChange}
                    className={formErrors.high_school ? 'error' : ''}
                  />
                  {formErrors.high_school && <span className="error-text">{formErrors.high_school}</span>}
                </div>

                <div className="input-group">
                  <label>Program *</label>
                  <select
                    name="program"
                    value={formData.program}
                    onChange={handleInputChange}
                    className={formErrors.program ? 'error' : ''}
                  >
                    <option value="">Choose program</option>
                    {programs.map(program => (
                      <option key={program} value={program}>{program}</option>
                    ))}
                  </select>
                  {formErrors.program && <span className="error-text">{formErrors.program}</span>}
                </div>

                <div className="input-group">
                  <label>Student Type</label>
                  <select
                    name="student_type"
                    value={formData.student_type}
                    onChange={handleInputChange}
                  >
                    <option value="new">New Student</option>
                    <option value="transferee">Transferee</option>
                  </select>
                </div>
              </div>
            </div>
          )}

          {/* Step 3: Documents */}
          {currentStep === 3 && (
            <div className="form-step">
              <h2>Required Documents</h2>
              <p className="step-description">Upload clear photos or scans of these documents</p>
              
              <div className="documents-grid">
                <FileUpload 
                  label="2x2 ID Photo" 
                  fileType="id_photo" 
                  accept="image/*" 
                  onFileUpload={handleFileUpload} 
                  currentFile={formData.id_photo}
                  error={formErrors.id_photo}
                />
                
                <FileUpload 
                  label="Good Moral Certificate" 
                  fileType="good_moral" 
                  accept=".pdf,.jpg,.jpeg,.png" 
                  onFileUpload={handleFileUpload} 
                  currentFile={formData.good_moral}
                  error={formErrors.good_moral}
                />
                
                <FileUpload 
                  label="Report Card (Form 138)" 
                  fileType="report_card" 
                  accept=".pdf,.jpg,.jpeg,.png" 
                  onFileUpload={handleFileUpload} 
                  currentFile={formData.report_card}
                  error={formErrors.report_card}
                />
                
                <FileUpload 
                  label="Diploma" 
                  fileType="diploma" 
                  accept=".pdf,.jpg,.jpeg,.png" 
                  onFileUpload={handleFileUpload} 
                  currentFile={formData.diploma}
                  error={formErrors.diploma}
                />
              </div>

              <div className="notes">
                <h3>üìù Important Notes</h3>
                <ul>
                  <li>Bring original documents on exam day</li>
                  <li>Arrive 30 minutes before your scheduled time</li>
                  <li>Check your email for exam details</li>
                </ul>
              </div>
            </div>
          )}

          {/* Navigation Buttons */}
          <div className="form-actions">
            {currentStep > 1 && (
              <button type="button" onClick={prevStep} className="btn btn-secondary">
                Back
              </button>
            )}
            
            {currentStep < 3 ? (
              <button type="button" onClick={nextStep} className="btn btn-primary">
                Continue
              </button>
            ) : (
              <button type="submit" disabled={loading} className="btn btn-primary">
                {loading ? 'Submitting...' : 'Submit Application'}
              </button>
            )}
          </div>
        </form>
      </div>
    </div>
  );
};

export default AdmissionForm;
