sourcesContent:
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import FileUpload from './FileUpload';
import './AdmissionForm.css';

const AdmissionForm = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [currentStep, setCurrentStep] = useState(1);
  const [formErrors, setFormErrors] = useState({});

  const [formData, setFormData] = useState({
    // Personal Information
    first_name: '',
    middle_name: '',
    last_name: '',
    email: '',
    birthday: '',
    sex: '',
    address: '',
    contact_number: '',
    
    // Emergency Contact
    emergency_contact_name: '',
    emergency_contact_number: '',
    
    // Educational Background
    junior_high_school: '',
    senior_high_school: '',
    desired_program: '',
    year_level: '',
    student_type: 'New',
    last_school_attended: 'N/A',
    
    // Documents
    picture_2x2: '',
    good_moral_cert: '',
    form_138: '',
    graduation_cert: ''
  });

  const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:4001';

  // Form steps configuration
  const formSteps = [
    { number: 1, title: 'Personal Info', icon: 'ðŸ‘¤' },
    { number: 2, title: 'Emergency Contact', icon: 'ðŸ†˜' },
    { number: 3, title: 'Education', icon: 'ðŸŽ“' },
    { number: 4, title: 'Documents', icon: 'ðŸ“„' },
    { number: 5, title: 'Review', icon: 'âœ…' }
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleFileUpload = (fileType, fileUrl) => {
    setFormData(prev => ({
      ...prev,
      [fileType]: fileUrl
    }));
  };

  const validateStep = (step) => {
    const errors = {};

    switch (step) {
      case 1:
        if (!formData.first_name.trim()) errors.first_name = 'First name is required';
        if (!formData.last_name.trim()) errors.last_name = 'Last name is required';
        if (!formData.email.trim()) errors.email = 'Email is required';
        else if (!/\S+@\S+\.\S+/.test(formData.email)) errors.email = 'Email is invalid';
        if (!formData.birthday) errors.birthday = 'Birthday is required';
        if (!formData.sex) errors.sex = 'Sex is required';
        if (!formData.address.trim()) errors.address = 'Address is required';
        if (!formData.contact_number.trim()) errors.contact_number = 'Contact number is required';
        break;

      case 2:
        if (!formData.emergency_contact_name.trim()) errors.emergency_contact_name = 'Emergency contact name is required';
        if (!formData.emergency_contact_number.trim()) errors.emergency_contact_number = 'Emergency contact number is required';
        break;

      case 3:
        if (!formData.junior_high_school.trim()) errors.junior_high_school = 'Junior high school is required';
        if (!formData.senior_high_school.trim()) errors.senior_high_school = 'Senior high school is required';
        if (!formData.desired_program.trim()) errors.desired_program = 'Desired program is required';
        if (!formData.year_level) errors.year_level = 'Year level is required';
        if (formData.student_type === 'Transferee' && !formData.last_school_attended.trim()) {
          errors.last_school_attended = 'Last school attended is required for transferees';
        }
        break;

      case 4:
        const requiredFiles = ['picture_2x2', 'good_moral_cert', 'form_138', 'graduation_cert'];
        requiredFiles.forEach(fileType => {
          if (!formData[fileType]) {
            errors[fileType] = 'This document is required';
          }
        });
        break;
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const nextStep = () => {
    if (validateStep(currentStep)) {
      setCurrentStep(prev => Math.min(prev + 1, formSteps.length));
    }
  };

  const prevStep = () => {
    setCurrentStep(prev => Math.max(prev - 1, 1));
  };

  const sendEmailConfirmation = async (admissionData) => {
    try {
      const studentName = `${admissionData.first_name} ${
        admissionData.middle_name ? admissionData.middle_name + ' ' : ''
      }${admissionData.last_name}`;

      const response = await fetch(`${API_BASE_URL}/send-confirmation`, {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({
          student_name: studentName,
          student_email: admissionData.email,
          exam_schedule: admissionData.exam_schedule,
          year_level: admissionData.year_level
        }),
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`HTTP ${response.status}: ${errorText}`);
      }

      const result = await response.json();
      return result.success;
    } catch (error) {
      console.error('âŒ Email request failed:', error);
      return false;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    if (!validateStep(4)) {
      setCurrentStep(4); // Go to documents step if there are errors
      setLoading(false);
      return;
    }

    try {
      const examSchedule = new Date();
      examSchedule.setDate(examSchedule.getDate() + 7);

      const { data, error } = await supabase
        .from('student_admissions')
        .insert([{
          ...formData,
          exam_schedule: examSchedule.toISOString(),
          submitted_at: new Date().toISOString()
        }])
        .select();

      if (error) throw error;

      await sendEmailConfirmation({
        ...formData,
        exam_schedule: examSchedule.toISOString()
      });

      alert('âœ… Application submitted successfully! Check your email for exam details.');
      navigate('/applications');

    } catch (error) {
      console.error('Submission error:', error);
      alert('Error submitting application: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const renderStepIndicator = () => (
    <div className="step-indicator">
      {formSteps.map(step => (
        <div key={step.number} className={`step ${currentStep === step.number ? 'active' : ''} ${currentStep > step.number ? 'completed' : ''}`}>
          <div className="step-circle">
            {currentStep > step.number ? 'âœ“' : step.icon}
          </div>
          <span className="step-title">{step.title}</span>
          {step.number < formSteps.length && <div className="step-connector"></div>}
        </div>
      ))}
    </div>
  );

  const renderPersonalInfoStep = () => (
    <div className="form-section">
      <h3>ðŸ‘¤ Personal Information</h3>
      <div className="form-grid">
        <div className="form-group">
          <label>First Name *</label>
          <input 
            type="text" 
            name="first_name" 
            value={formData.first_name} 
            onChange={handleInputChange} 
            className={formErrors.first_name ? 'error' : ''}
          />
          {formErrors.first_name && <span className="error-message">{formErrors.first_name}</span>}
        </div>

        <div className="form-group">
          <label>Middle Name</label>
          <input 
            type="text" 
            name="middle_name" 
            value={formData.middle_name} 
            onChange={handleInputChange} 
          />
        </div>

        <div className="form-group">
          <label>Last Name *</label>
          <input 
            type="text" 
            name="last_name" 
            value={formData.last_name} 
            onChange={handleInputChange} 
            className={formErrors.last_name ? 'error' : ''}
          />
          {formErrors.last_name && <span className="error-message">{formErrors.last_name}</span>}
        </div>

        <div className="form-group">
          <label>Sex *</label>
          <select 
            name="sex" 
            value={formData.sex} 
            onChange={handleInputChange} 
            className={formErrors.sex ? 'error' : ''}
          >
            <option value="">Select Sex</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Prefer not to say">Prefer not to say</option>
          </select>
          {formErrors.sex && <span className="error-message">{formErrors.sex}</span>}
        </div>

        <div className="form-group">
          <label>Email *</label>
          <input 
            type="email" 
            name="email" 
            value={formData.email} 
            onChange={handleInputChange} 
            className={formErrors.email ? 'error' : ''}
          />
          {formErrors.email && <span className="error-message">{formErrors.email}</span>}
        </div>

        <div className="form-group">
          <label>Birthday *</label>
          <input 
            type="date" 
            name="birthday" 
            value={formData.birthday} 
            onChange={handleInputChange} 
            className={formErrors.birthday ? 'error' : ''}
          />
          {formErrors.birthday && <span className="error-message">{formErrors.birthday}</span>}
        </div>

        <div className="form-group full-width">
          <label>Address *</label>
          <input 
            type="text" 
            name="address" 
            value={formData.address} 
            onChange={handleInputChange} 
            className={formErrors.address ? 'error' : ''}
            placeholder="Complete address including city and zip code"
          />
          {formErrors.address && <span className="error-message">{formErrors.address}</span>}
        </div>

        <div className="form-group">
          <label>Contact Number *</label>
          <input 
            type="tel" 
            name="contact_number" 
            value={formData.contact_number} 
            onChange={handleInputChange} 
            className={formErrors.contact_number ? 'error' : ''}
            placeholder="09XXXXXXXXX"
          />
          {formErrors.contact_number && <span className="error-message">{formErrors.contact_number}</span>}
        </div>
      </div>
    </div>
  );

  const renderEmergencyContactStep = () => (
    <div className="form-section">
      <h3>ðŸ†˜ Emergency Contact</h3>
      <p className="section-description">Please provide contact information for emergencies</p>
      <div className="form-grid">
        <div className="form-group">
          <label>Emergency Contact Name *</label>
          <input 
            type="text" 
            name="emergency_contact_name" 
            value={formData.emergency_contact_name} 
            onChange={handleInputChange} 
            className={formErrors.emergency_contact_name ? 'error' : ''}
            placeholder="Full name of emergency contact"
          />
          {formErrors.emergency_contact_name && <span className="error-message">{formErrors.emergency_contact_name}</span>}
        </div>
        <div className="form-group">
          <label>Emergency Contact Number *</label>
          <input 
            type="tel" 
            name="emergency_contact_number" 
            value={formData.emergency_contact_number} 
            onChange={handleInputChange} 
            className={formErrors.emergency_contact_number ? 'error' : ''}
            placeholder="09XXXXXXXXX"
          />
          {formErrors.emergency_contact_number && <span className="error-message">{formErrors.emergency_contact_number}</span>}
        </div>
      </div>
    </div>
  );

  const renderEducationStep = () => (
    <div className="form-section">
      <h3>ðŸŽ“ Educational Background</h3>
      <div className="form-grid">
        <div className="form-group">
          <label>Junior High School *</label>
          <input 
            type="text" 
            name="junior_high_school" 
            value={formData.junior_high_school} 
            onChange={handleInputChange} 
            className={formErrors.junior_high_school ? 'error' : ''}
          />
          {formErrors.junior_high_school && <span className="error-message">{formErrors.junior_high_school}</span>}
        </div>

        <div className="form-group">
          <label>Senior High School *</label>
          <input 
            type="text" 
            name="senior_high_school" 
            value={formData.senior_high_school} 
            onChange={handleInputChange} 
            className={formErrors.senior_high_school ? 'error' : ''}
          />
          {formErrors.senior_high_school && <span className="error-message">{formErrors.senior_high_school}</span>}
        </div>

        <div className="form-group">
          <label>Desired Program *</label>
          <select 
            name="desired_program" 
            value={formData.desired_program} 
            onChange={handleInputChange} 
            className={formErrors.desired_program ? 'error' : ''}
          >
            <option value="">Select Program</option>
            <option value="Computer Science">Computer Science</option>
            <option value="Engineering">Engineering</option>
            <option value="Business Administration">Business Administration</option>
            <option value="Education">Education</option>
            <option value="Nursing">Nursing</option>
          </select>
          {formErrors.desired_program && <span className="error-message">{formErrors.desired_program}</span>}
        </div>

        <div className="form-group">
          <label>Year Level *</label>
          <select 
            name="year_level" 
            value={formData.year_level} 
            onChange={handleInputChange} 
            className={formErrors.year_level ? 'error' : ''}
          >
            <option value="">Select Year Level</option>
            <option value="Freshman">Freshman</option>
            <option value="Transferee">Transferee</option>
          </select>
          {formErrors.year_level && <span className="error-message">{formErrors.year_level}</span>}
        </div>

        <div className="form-group">
          <label>Student Type *</label>
          <select 
            name="student_type" 
            value={formData.student_type} 
            onChange={handleInputChange} 
          >
            <option value="New">New Student</option>
            <option value="Transferee">Transferee</option>
          </select>
        </div>

        {formData.student_type === 'Transferee' && (
          <div className="form-group">
            <label>Last School Attended *</label>
            <input
              type="text"
              name="last_school_attended"
              value={formData.last_school_attended}
              onChange={handleInputChange}
              className={formErrors.last_school_attended ? 'error' : ''}
            />
            {formErrors.last_school_attended && <span className="error-message">{formErrors.last_school_attended}</span>}
          </div>
        )}
      </div>
    </div>
  );

  const renderDocumentsStep = () => (
    <div className="form-section">
      <h3>ðŸ“„ Required Documents</h3>
      <p className="section-description">Please upload clear photos or scans of the following documents</p>
      <div className="file-uploads-grid">
        <FileUpload 
          label="2x2 ID Picture" 
          fileType="picture_2x2" 
          accept="image/*" 
          onFileUpload={handleFileUpload} 
          currentFile={formData.picture_2x2} 
          error={formErrors.picture_2x2}
          required 
        />
        <FileUpload 
          label="Certificate of Good Moral" 
          fileType="good_moral_cert" 
          accept=".pdf,.doc,.docx,image/*" 
          onFileUpload={handleFileUpload} 
          currentFile={formData.good_moral_cert} 
          error={formErrors.good_moral_cert}
          required 
        />
        <FileUpload 
          label="Form 138 (Report Card)" 
          fileType="form_138" 
          accept=".pdf,.doc,.docx,image/*" 
          onFileUpload={handleFileUpload} 
          currentFile={formData.form_138} 
          error={formErrors.form_138}
          required 
        />
        <FileUpload 
          label="Certificate of Graduation" 
          fileType="graduation_cert" 
          accept=".pdf,.doc,.docx,image/*" 
          onFileUpload={handleFileUpload} 
          currentFile={formData.graduation_cert} 
          error={formErrors.graduation_cert}
          required 
        />
      </div>
    </div>
  );

  const renderReviewStep = () => (
    <div className="form-section">
      <h3>âœ… Review Your Application</h3>
      <div className="review-section">
        <h4>Personal Information</h4>
        <div className="review-grid">
          <div><strong>Name:</strong> {formData.first_name} {formData.middle_name} {formData.last_name}</div>
          <div><strong>Email:</strong> {formData.email}</div>
          <div><strong>Birthday:</strong> {formData.birthday}</div>
          <div><strong>Contact:</strong> {formData.contact_number}</div>
        </div>
      </div>

      <div className="review-section">
        <h4>Educational Background</h4>
        <div className="review-grid">
          <div><strong>Junior High:</strong> {formData.junior_high_school}</div>
          <div><strong>Senior High:</strong> {formData.senior_high_school}</div>
          <div><strong>Program:</strong> {formData.desired_program}</div>
          <div><strong>Student Type:</strong> {formData.student_type}</div>
        </div>
      </div>

      <div className="review-section">
        <h4>Documents Uploaded</h4>
        <div className="review-grid">
          <div><strong>2x2 Picture:</strong> {formData.picture_2x2 ? 'âœ… Uploaded' : 'âŒ Missing'}</div>
          <div><strong>Good Moral:</strong> {formData.good_moral_cert ? 'âœ… Uploaded' : 'âŒ Missing'}</div>
          <div><strong>Form 138:</strong> {formData.form_138 ? 'âœ… Uploaded' : 'âŒ Missing'}</div>
          <div><strong>Graduation Cert:</strong> {formData.graduation_cert ? 'âœ… Uploaded' : 'âŒ Missing'}</div>
        </div>
      </div>

      <div className="important-notes">
        <h4>ðŸ“‹ Important Examination Notes</h4>
        <div className="notes-content">
          <div className="note-item">
            <strong>ðŸ–Šï¸ What to Bring on Exam Day:</strong>
            <ul>
              <li>Original documents (no photocopies)</li>
              <li>Ballpen (Black ink only)</li>
              <li>Valid ID for verification</li>
            </ul>
          </div>
          
          <div className="note-item warning">
            <strong>âš ï¸ Important Reminder:</strong>
            <p>Bring the ORIGINAL documents that you uploaded. Your exam schedule will be sent to your email.</p>
          </div>
        </div>
      </div>
    </div>
  );

  const renderStepContent = () => {
    switch (currentStep) {
      case 1: return renderPersonalInfoStep();
      case 2: return renderEmergencyContactStep();
      case 3: return renderEducationStep();
      case 4: return renderDocumentsStep();
      case 5: return renderReviewStep();
      default: return renderPersonalInfoStep();
    }
  };

  return (
    <div className="admission-form-page">
      <div className="form-container">
        <div className="form-header">
          <h1>Student Application Form</h1>
          <p>Complete all steps to submit your application</p>
        </div>

        {renderStepIndicator()}

        <form onSubmit={handleSubmit} className="admission-form">
          {renderStepContent()}

          <div className="form-navigation">
            {currentStep > 1 && (
              <button type="button" onClick={prevStep} className="nav-button prev-button">
                â† Previous
              </button>
            )}
            
            {currentStep < formSteps.length - 1 && (
              <button type="button" onClick={nextStep} className="nav-button next-button">
                Next â†’
              </button>
            )}
            
            {currentStep === formSteps.length - 1 && (
              <button type="submit" disabled={loading} className="submit-button">
                {loading ? 'Submitting...' : 'Submit Application'}
              </button>
            )}
          </div>

          <div className="progress-info">
            <span>Step {currentStep} of {formSteps.length - 1}</span>
            <div className="progress-bar">
              <div 
                className="progress-fill" 
                style={{ width: `${((currentStep - 1) / (formSteps.length - 1)) * 100}%` }}
              ></div>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AdmissionForm;
