import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './StudentDashboard.css';
import CertificateOfRegistration from './CertificateOfRegistration';

const StudentDashboard = ({ studentData }) => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState('dashboard');
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [showNotifications, setShowNotifications] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      const mobile = window.innerWidth < 768;
      setIsMobile(mobile);
      if (mobile) {
        setSidebarOpen(false);
      }
    };

    window.addEventListener('resize', handleResize);
    handleResize(); // Initial check
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleLogout = () => {
    navigate('/login');
  };

  // Mock notifications data
  useEffect(() => {
    const mockNotifications = [
      { id: 1, type: 'academic', title: 'Grades Updated', message: 'Your midterm grades are now available', timestamp: '2024-01-15', read: false },
      { id: 2, type: 'system', title: 'System Maintenance', message: 'Portal maintenance scheduled for Sunday', timestamp: '2024-01-14', read: false },
      { id: 3, type: 'academic', title: 'Document Ready', message: 'Your COR is ready for download', timestamp: '2024-01-13', read: true },
    ];
    setNotifications(mockNotifications);
    setUnreadCount(mockNotifications.filter(n => !n.read).length);
  }, []);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const toggleNotifications = () => {
    setShowNotifications(!showNotifications);
  };

  const markAllAsRead = () => {
    const updatedNotifications = notifications.map(notif => ({ ...notif, read: true }));
    setNotifications(updatedNotifications);
    setUnreadCount(0);
  };

  const handleCORNavigation = () => {
    setActiveSection('cor');
  };

  const handleCloseCOR = () => {
    setActiveSection('dashboard');
  };

  if (!studentData) {
    return (
      <div className="admin-dashboard-loading">
        <div className="loading-spinner"></div>
        <p>Loading student data...</p>
      </div>
    );
  }

  const navItems = [
    { id: 'dashboard', label: 'Dashboard', lineIcon: 'ðŸ“Š', shortLabel: 'Dash' },
    { id: 'academics', label: 'Academic Records', lineIcon: 'ðŸ“š', shortLabel: 'Academics' },
    { id: 'documents', label: 'Online Documents', lineIcon: 'ðŸ“„', shortLabel: 'Docs' },
    { id: 'cor', label: 'COR Generator', lineIcon: 'ðŸŽ“', shortLabel: 'COR' },
    { id: 'scheduler', label: 'Request Scheduler', lineIcon: 'ðŸ“…', shortLabel: 'Schedule' },
    { id: 'applications', label: 'Applications', lineIcon: 'ðŸ“‹', shortLabel: 'Apps' },
    { id: 'profile', label: 'My Profile', lineIcon: 'ðŸ‘¤', shortLabel: 'Profile' },
    { id: 'settings', label: 'Account Settings', lineIcon: 'âš™ï¸', shortLabel: 'Settings' },
    { id: 'resources', label: 'Campus Resources', lineIcon: 'ðŸ›ï¸', shortLabel: 'Resources' },
  ];

  return (
    <div className="admin-dashboard">
      {/* Light Green Sidebar */}
      <div className={`dashboard-sidebar green-sidebar ${sidebarOpen ? 'open' : 'closed'} ${isMobile ? 'mobile' : ''}`}>
        <div className="sidebar-header">
          <div className="sidebar-logo">
            <img src="/logo-ptc.png" alt="PTC Logo" />
          </div>
          {sidebarOpen && (
            <div className="sidebar-title">
              <h3>PTC Student</h3>
              <span className="admin-subtitle">Portal</span>
            </div>
          )}
          {!sidebarOpen && !isMobile && (
            <div className="sidebar-title-collapsed">
              <span>PTC</span>
            </div>
          )}
        </div>
        
        <nav className="sidebar-nav">
          {navItems.map(item => (
            <button
              key={item.id}
              className={`nav-item green-nav-item ${activeSection === item.id ? 'active' : ''}`}
              onClick={() => {
                setActiveSection(item.id);
                if (isMobile) setSidebarOpen(false);
              }}
              title={item.label}
            >
              <span className="nav-icon-green">{item.lineIcon}</span>
              {sidebarOpen ? (
                <span className="nav-label">{item.label}</span>
              ) : (
                !isMobile && <span className="nav-label-collapsed">{item.shortLabel}</span>
              )}
            </button>
          ))}
        </nav>
        
        <div className="sidebar-footer">
          <div className="user-info">
            <div className="user-avatar">
              {studentData.first_name?.[0]}{studentData.last_name?.[0]}
            </div>
            {sidebarOpen && (
              <div className="user-details">
                <div className="user-name">{studentData.first_name} {studentData.last_name}</div>
                <div className="user-id">{studentData.student_number}</div>
              </div>
            )}
          </div>
          <button className="nav-item green-nav-item logout-nav" onClick={handleLogout} title="Logout">
            <span className="nav-icon-green">ðŸšª</span>
            {sidebarOpen && <span className="nav-label">Logout</span>}
          </button>
        </div>
      </div>

      {/* Overlay for mobile when sidebar is open */}
      {isMobile && sidebarOpen && (
        <div className="sidebar-overlay" onClick={() => setSidebarOpen(false)}></div>
      )}

      {/* Main Content Area */}
      <div className="dashboard-main">
        {/* Dashboard Header */}
        <div className="dashboard-header green-header">
          <div className="header-content">
            <div className="header-left">
              <button className="mobile-menu-btn" onClick={toggleSidebar}>
                <span className="menu-icon">â˜°</span>
              </button>
              <div className="header-title-section">
                <h1 className="page-title">
                  {navItems.find(item => item.id === activeSection)?.label || 'Dashboard'}
                </h1>
                <p className="page-subtitle">Welcome back, {studentData.first_name}!</p>
              </div>
            </div>
            <div className="admin-info">
              <div className="header-actions">
                <div className="search-box-mobile">
                  <input type="text" placeholder="Search..." className="search-input" />
                  <span className="search-icon">ðŸ”</span>
                </div>
                
                <div className="notification-wrapper">
                  <button className="icon-btn notification-btn" onClick={toggleNotifications}>
                    <span className="icon">ðŸ””</span>
                    {unreadCount > 0 && (
                      <span className="notification-indicator">{unreadCount}</span>
                    )}
                  </button>
                  {showNotifications && (
                    <div className="notification-dropdown">
                      <div className="notification-header">
                        <h3>Notifications</h3>
                        <button className="mark-read-btn" onClick={markAllAsRead}>
                          Mark all as read
                        </button>
                      </div>
                      <div className="notification-list">
                        {notifications.map(notification => (
                          <div key={notification.id} className={`notification-item ${notification.read ? 'read' : 'unread'}`}>
                            <div className="notification-icon">
                              {notification.type === 'academic' ? 'ðŸ“š' : 'âš™ï¸'}
                            </div>
                            <div className="notification-content">
                              <div className="notification-title">{notification.title}</div>
                              <div className="notification-message">{notification.message}</div>
                              <div className="notification-time">{notification.timestamp}</div>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              </div>
              
              <div className="user-profile-mobile">
                <div className="admin-welcome">Hi, {studentData.first_name}</div>
                <div className="admin-avatar">
                  {studentData.first_name?.[0]}{studentData.last_name?.[0]}
                </div>
              </div>
            </div>
          </div>
          
          {/* Quick Stats Bar */}
          <div className="quick-stats-bar">
            <div className="stat-item">
