// Scheduler Section Component - Enhanced for Mobile
const SchedulerSection = () => {
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedTime, setSelectedTime] = useState('');
  const [purpose, setPurpose] = useState('');
  const [currentStep, setCurrentStep] = useState(1);

  const timeSlots = ['09:00 AM', '10:00 AM', '11:00 AM', '01:00 PM', '02:00 PM', '03:00 PM'];

  const handleSubmitRequest = (e) => {
    e.preventDefault();
    alert('Appointment requested for ' + selectedDate + ' at ' + selectedTime + '\nPurpose: ' + purpose);
    setCurrentStep(1);
    setSelectedDate('');
    setSelectedTime('');
    setPurpose('');
  };

  const nextStep = () => setCurrentStep(currentStep + 1);
  const prevStep = () => setCurrentStep(currentStep - 1);

  return (
    <div className="section-content">
      <div className="scheduler-container responsive-scheduler">
        {/* Progress Steps */}
        <div className="progress-steps">
          <div className={`step ${currentStep >= 1 ? 'active' : ''}`}>
            <div className="step-number">1</div>
            <span className="step-label">Date & Time</span>
          </div>
          <div className={`step ${currentStep >= 2 ? 'active' : ''}`}>
            <div className="step-number">2</div>
            <span className="step-label">Purpose</span>
          </div>
          <div className={`step ${currentStep >= 3 ? 'active' : ''}`}>
            <div className="step-number">3</div>
            <span className="step-label">Review</span>
          </div>
        </div>

        <div className="scheduler-content">
          {/* Step 1: Date & Time */}
          {currentStep === 1 && (
            <div className="scheduler-step">
              <h3>Select Date & Time</h3>
              <div className="form-group">
                <label>Preferred Date</label>
                <input
                  type="date"
                  value={selectedDate}
                  onChange={(e) => setSelectedDate(e.target.value)}
                  min={new Date().toISOString().split('T')[0]}
                  required
                />
              </div>
              <div className="form-group">
                <label>Preferred Time</label>
                <div className="time-slots">
                  {timeSlots.map(slot => (
                    <button
                      key={slot}
                      type="button"
                      className={`time-slot ${selectedTime === slot ? 'selected' : ''}`}
                      onClick={() => setSelectedTime(slot)}
                    >
                      {slot}
                    </button>
                  ))}
                </div>
              </div>
              <div className="step-actions">
                <button type="button" className="btn-next" onClick={nextStep} disabled={!selectedDate || !selectedTime}>
                  Next: Purpose
                </button>
              </div>
            </div>
          )}

          {/* Step 2: Purpose */}
          {currentStep === 2 && (
            <div className="scheduler-step">
              <h3>Purpose of Request</h3>
              <div className="form-group">
                <label>Please specify the purpose for requesting certified true copies</label>
                <textarea
                  value={purpose}
                  onChange={(e) => setPurpose(e.target.value)}
                  placeholder="e.g., Scholarship application, employment requirements, further studies..."
                  rows={4}
                  required
                />
              </div>
              <div className="step-actions">
                <button type="button" className="btn-prev" onClick={prevStep}>
                  Back
                </button>
                <button type="button" className="btn-next" onClick={nextStep} disabled={!purpose}>
                  Next: Review
                </button>
              </div>
            </div>
          )}

          {/* Step 3: Review */}
          {currentStep === 3 && (
            <div className="scheduler-step">
              <h3>Review Your Request</h3>
              <div className="review-details">
                <div className="review-item">
                  <label>Date:</label>
                  <span>{selectedDate}</span>
                </div>
                <div className="review-item">
                  <label>Time:</label>
                  <span>{selectedTime}</span>
                </div>
                <div className="review-item">
                  <label>Purpose:</label>
                  <span>{purpose}</span>
                </div>
              </div>
              <div className="step-actions">
                <button type="button" className="btn-prev" onClick={prevStep}>
                  Back
                </button>
                <button type="submit" className="btn-confirm" onClick={handleSubmitRequest}>
                  ðŸ“… Schedule Appointment
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Scheduler Info Sidebar */}
        <div className="scheduler-info">
          <h4>ðŸ“ Location</h4>
          <p>Registrar's Office<br />Main Building, 2nd Floor</p>
          
          <h4>â° Office Hours</h4>
          <p>Monday-Friday<br />8:00 AM - 5:00 PM</p>
          
          <h4>ðŸ“‹ Requirements</h4>
          <p>â€¢ Valid ID<br />â€¢ Document Request Form<br />â€¢ Student ID</p>

          <div className="info-note">
            <strong>Note:</strong> Please bring all required documents to your appointment.
          </div>
        </div>
      </div>
    </div>
  );
};

